import React from "react";

const Input = ({
  label,
  id,
  type = "text",
  value,
  onChange,
  placeholder = "",
  required = false,
  error = null,
  className = "",
  fullWidth = true,
  disabled = false,
}) => {
  const baseInputStyles =
    "border rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all";
  const errorStyles = error ? "border-red-500" : "border-gray-300";
  const disabledStyles = disabled ? "bg-gray-100 text-gray-500" : "";
  const widthStyles = fullWidth ? "w-full" : "";

  const inputClasses = `${baseInputStyles} ${errorStyles} ${disabledStyles} ${widthStyles} ${className}`;

  return (
    <div className={`mb-4 ${widthStyles}`}>
      {label && (
        <label className="block mb-1 font-medium text-gray-700" htmlFor={id}>
          {label} {required && <span className="text-red-500">*</span>}
        </label>
      )}

      <input
        id={id}
        type={type}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        required={required}
        disabled={disabled}
        className={inputClasses}
      />

      {error && <p className="mt-1 text-sm text-red-600">{error}</p>}
    </div>
  );
};

export default Input;
