import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Layout from "../../components/Layout";
import Button from "../../components/Button";
import Spinner from "../../components/Spinner";
import { usePatients } from "../../context/PatientContext";

const SearchPatient = () => {
  const [patientId, setPatientId] = useState("");
  const [searchError, setSearchError] = useState("");
  const [patient, setPatient] = useState(null);
  const [autoSearchDone, setAutoSearchDone] = useState(false);
  const { findPatientById, loading } = usePatients();
  const location = useLocation();

  // Add responsive state
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const isMobile = windowWidth < 768;
  const isTablet = windowWidth >= 768 && windowWidth < 1024;

  // Handle window resize for responsiveness
  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Auto-search if patientId is provided in URL query parameters
  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const idFromUrl = searchParams.get("patientId");

    if (idFromUrl && !autoSearchDone) {
      setPatientId(idFromUrl);
      searchPatient(idFromUrl);
      setAutoSearchDone(true);
    }
  }, [location.search]);

  const searchPatient = async (id) => {
    if (!id.trim()) {
      setSearchError("Please enter a patient ID");
      return;
    }

    try {
      const foundPatient = await findPatientById(id);

      if (!foundPatient) {
        setSearchError("No patient found with this ID");
        setPatient(null);
      } else {
        setPatient(foundPatient);
        setSearchError("");
      }
    } catch (err) {
      console.error("Error searching for patient:", err);
      setSearchError("An error occurred while searching");
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    searchPatient(patientId);
  };

  // ==== STYLES ====

  // Page container styles
  const pageContainerStyle = {
    maxWidth: isMobile ? "100%" : isTablet ? "90%" : "1100px",
    margin: "0 auto",
    padding: isMobile ? "16px" : isTablet ? "24px" : "32px",
    width: "100%",
    boxSizing: "border-box",
  };

  // Header section styles
  const headerSectionStyle = {
    marginBottom: isMobile ? "24px" : "32px",
  };

  // Header title styles
  const headerTitleStyle = {
    fontSize: isMobile ? "20px" : isTablet ? "24px" : "28px",
    fontWeight: "700",
    color: "#1E293B",
    borderLeft: "4px solid #2563EB",
    paddingLeft: "12px",
    marginBottom: "8px",
  };

  // Header description styles
  const headerDescriptionStyle = {
    fontSize: isMobile ? "14px" : "16px",
    color: "#64748B",
    marginTop: "8px",
  };

  // Search form card styles
  const searchFormCardStyle = {
    backgroundColor: "#ffffff",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.05)",
    padding: isMobile ? "16px" : "24px",
    marginBottom: "24px",
    border: "1px solid #E2E8F0",
  };

  // Form styles
  const formStyle = {
    display: "flex",
    flexDirection: isMobile ? "column" : "row",
    gap: "16px",
    alignItems: isMobile ? "stretch" : "flex-end",
    width: "100%",
  };

  // Form group styles
  const formGroupStyle = {
    flexGrow: 1,
    position: "relative",
  };

  // Label styles
  const labelStyle = {
    display: "block",
    marginBottom: "8px",
    fontWeight: "500",
    fontSize: isMobile ? "14px" : "15px",
    color: "#334155",
  };

  // Input styles
  const inputStyle = {
    width: "100%",
    padding: "12px 16px",
    fontSize: isMobile ? "14px" : "15px",
    borderRadius: "8px",
    border: searchError ? "1px solid #EF4444" : "1px solid #CBD5E1",
    backgroundColor: "#FFFFFF",
    boxSizing: "border-box",
    outline: "none",
    transition: "border-color 0.2s ease, box-shadow 0.2s ease",
  };

  // Input focus/blur handlers
  const focusStyle = (e) => {
    e.target.style.borderColor = "#3B82F6";
    e.target.style.boxShadow = "0 0 0 3px rgba(59, 130, 246, 0.1)";
  };

  const blurStyle = (e) => {
    if (searchError) {
      e.target.style.borderColor = "#EF4444";
      e.target.style.boxShadow = "none";
    } else {
      e.target.style.borderColor = "#CBD5E1";
      e.target.style.boxShadow = "none";
    }
  };

  // Error message styles
  const errorMessageStyle = {
    color: "#EF4444",
    fontSize: "13px",
    marginTop: "4px",
    position: "absolute",
  };

  // Button container styles
  const buttonContainerStyle = {
    display: "flex",
    alignItems: "center",
    // marginBottom: isMobile ? "0" : "2px",
  };

  // Search button icon styles
  const searchIconStyle = {
    width: "16px",
    height: "16px",
    marginRight: "8px",
  };

  // Patient card container styles
  const patientCardStyle = {
    backgroundColor: "#FFFFFF",
    borderRadius: "12px",
    overflow: "hidden",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.05)",
    border: "1px solid #E2E8F0",
  };

  // Patient card flex container
  const patientCardFlexStyle = {
    display: "flex",
    flexDirection: isMobile ? "column" : "row",
  };

  // Patient image container styles
  const patientImageContainerStyle = {
    width: isMobile ? "100%" : "33.333%",
    backgroundColor: "#F8FAFC",
  };

  // No image placeholder styles
  const noImagePlaceholderStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    height: isMobile ? "200px" : "100%",
    backgroundColor: "#F1F5F9",
    padding: "24px",
  };

  // No image icon styles
  const noImageIconStyle = {
    width: "64px",
    height: "64px",
    marginBottom: "8px",
    color: "#94A3B8",
  };

  // No image text styles
  const noImageTextStyle = {
    textAlign: "center",
    color: "#64748B",
  };

  // Patient info container styles
  const patientInfoContainerStyle = {
    width: isMobile ? "100%" : "66.666%",
    padding: isMobile ? "20px" : "24px",
  };

  // Patient header styles
  const patientHeaderStyle = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: isMobile ? "flex-start" : "center",
    marginBottom: "24px",
    flexDirection: isMobile ? "column" : "row",
    gap: isMobile ? "12px" : "0",
  };

  // Patient name styles
  const patientNameStyle = {
    fontSize: isMobile ? "20px" : "24px",
    fontWeight: "700",
    color: "#1E293B",
  };

  // Patient ID badge styles
  const patientIdBadgeStyle = {
    backgroundColor: "#DBEAFE",
    color: "#1D4ED8",
    fontSize: "12px",
    padding: "4px 12px",
    borderRadius: "9999px",
    fontWeight: "500",
    display: "inline-block",
  };

  // Patient details grid styles
  const patientDetailsGridStyle = {
    display: "grid",
    gridTemplateColumns: isMobile ? "1fr" : "repeat(2, 1fr)",
    gap: "16px",
    marginBottom: "24px",
  };

  // Patient detail label styles
  const patientDetailLabelStyle = {
    fontSize: "13px",
    fontWeight: "500",
    color: "#64748B",
    marginBottom: "4px",
  };

  // Patient detail value styles
  const patientDetailValueStyle = {
    fontSize: isMobile ? "16px" : "18px",
    color: "#334155",
  };

  // Patient results section styles
  const patientResultsSectionStyle = {
    borderTop: "1px solid #E2E8F0",
    paddingTop: "16px",
  };

  // Patient results title styles
  const patientResultsTitleStyle = {
    fontSize: isMobile ? "16px" : "18px",
    fontWeight: "600",
    color: "#1E293B",
    marginBottom: "12px",
  };

  // Test results content styles
  const testResultsContentStyle = {
    backgroundColor: "#EFF6FF",
    color: "#1E40AF",
    padding: "16px",
    borderRadius: "8px",
    fontSize: isMobile ? "14px" : "15px",
    lineHeight: "1.5",
  };

  // No results message styles
  const noResultsMessageStyle = {
    color: "#64748B",
    fontStyle: "italic",
  };

  // Actions container styles
  const actionsContainerStyle = {
    marginTop: "24px",
    display: "flex",
    justifyContent: "flex-end",
  };

  // No patient found card styles
  const noPatientCardStyle = {
    backgroundColor: "#FFFFFF",
    padding: "24px",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.05)",
    textAlign: "center",
    border: "1px solid #E2E8F0",
  };

  // No patient icon styles
  const noPatientIconStyle = {
    width: "48px",
    height: "48px",
    margin: "0 auto 16px auto",
    color: "#EF4444",
  };

  // No patient title styles
  const noPatientTitleStyle = {
    fontSize: isMobile ? "16px" : "18px",
    fontWeight: "500",
    color: "#111827",
    marginBottom: "8px",
  };

  // No patient message styles
  const noPatientMessageStyle = {
    color: "#64748B",
    marginBottom: "16px",
  };

  // Patient ID highlight styles
  const patientIdHighlightStyle = {
    fontWeight: "600",
  };

  return (
    <Layout>
      <div style={pageContainerStyle}>
        <div style={headerSectionStyle}>
          <h1 style={headerTitleStyle}>Patient Lookup</h1>
          <p style={headerDescriptionStyle}>
            Enter the patient ID to view their details and test results
          </p>
        </div>

        <div style={searchFormCardStyle}>
          <form onSubmit={handleSearch} style={formStyle}>
            <div style={formGroupStyle}>
              <label style={labelStyle} htmlFor="patientId">
                Patient ID
              </label>
              <input
                id="patientId"
                type="text"
                style={inputStyle}
                value={patientId}
                onChange={(e) => {
                  setPatientId(e.target.value);
                  setSearchError("");
                }}
                placeholder="Enter patient ID (e.g., P001)"
                onFocus={focusStyle}
                onBlur={blurStyle}
                autoFocus={autoSearchDone}
              />
              {searchError && (
                <div style={errorMessageStyle}>{searchError}</div>
              )}
            </div>
            <div style={buttonContainerStyle}>
              <Button
                type="submit"
                variant="primary"
                disabled={loading}
                style={{
                  width: isMobile ? "100%" : "auto",
                  padding: "12px 20px",
                  fontSize: "15px",
                  borderRadius: "8px",
                }}
              >
                {loading ? (
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                    }}
                  >
                    <Spinner size="small" color="white" />
                    <span style={{ marginLeft: "8px" }}>Searching...</span>
                  </div>
                ) : (
                  <div style={{ display: "flex", alignItems: "center" }}>
                    <svg
                      style={searchIconStyle}
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                      />
                    </svg>
                    Search
                  </div>
                )}
              </Button>
            </div>
          </form>
        </div>

        {patient && (
          <div style={patientCardStyle}>
            <div style={patientCardFlexStyle}>
              <div style={patientImageContainerStyle}>
                {patient.image ? (
                  <div style={{ height: "100%" }}>
                    <img
                      src={patient.image}
                      alt={patient.name}
                      style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                      }}
                    />
                  </div>
                ) : (
                  <div style={noImagePlaceholderStyle}>
                    <div style={noImageTextStyle}>
                      <svg
                        style={noImageIconStyle}
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                        />
                      </svg>
                      <p>No image available</p>
                    </div>
                  </div>
                )}
              </div>

              <div style={patientInfoContainerStyle}>
                <div style={patientHeaderStyle}>
                  <h2 style={patientNameStyle}>{patient.name}</h2>
                  <div style={patientIdBadgeStyle}>
                    Patient ID: {patient.id}
                  </div>
                </div>

                <div style={patientDetailsGridStyle}>
                  <div>
                    <h3 style={patientDetailLabelStyle}>Age</h3>
                    <p style={patientDetailValueStyle}>{patient.age} years</p>
                  </div>
                  {patient.gender && (
                    <div>
                      <h3 style={patientDetailLabelStyle}>Gender</h3>
                      <p style={patientDetailValueStyle}>{patient.gender}</p>
                    </div>
                  )}
                  {patient.contactNumber && (
                    <div>
                      <h3 style={patientDetailLabelStyle}>Contact Number</h3>
                      <p style={patientDetailValueStyle}>
                        {patient.contactNumber}
                      </p>
                    </div>
                  )}
                  {patient.email && (
                    <div>
                      <h3 style={patientDetailLabelStyle}>Email</h3>
                      <p style={patientDetailValueStyle}>{patient.email}</p>
                    </div>
                  )}
                </div>

                <div style={patientResultsSectionStyle}>
                  <h3 style={patientResultsTitleStyle}>Test Results</h3>
                  {patient.results ? (
                    <div style={testResultsContentStyle}>{patient.results}</div>
                  ) : (
                    <p style={noResultsMessageStyle}>
                      No test results available
                    </p>
                  )}
                </div>

                <div style={actionsContainerStyle}>
                  <Button
                    variant="secondary"
                    size="small"
                    onClick={() => {
                      setPatient(null);
                      setPatientId("");
                    }}
                    style={{
                      padding: "10px 16px",
                      fontSize: "14px",
                      borderRadius: "8px",
                    }}
                  >
                    Clear Results
                  </Button>
                </div>
              </div>
            </div>
          </div>
        )}

        {!patient && !loading && patientId && searchError && (
          <div style={noPatientCardStyle}>
            <div style={{ color: "#EF4444" }}>
              <svg
                style={noPatientIconStyle}
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                />
              </svg>
            </div>
            <h3 style={noPatientTitleStyle}>No Results Found</h3>
            <p style={noPatientMessageStyle}>
              We couldn't find a patient with the ID:{" "}
              <span style={patientIdHighlightStyle}>{patientId}</span>
            </p>
            <Button
              variant="secondary"
              size="small"
              onClick={() => {
                setPatientId("");
                setSearchError("");
              }}
              style={{
                padding: "10px 16px",
                fontSize: "14px",
                borderRadius: "8px",
              }}
            >
              Try Again
            </Button>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default SearchPatient;
