import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import Input from "../components/Input";
import Button from "../components/Button";
import Spinner from "../components/Spinner";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("lab");
  const { login, loading, error, isAuthenticated } = useAuth();
  const navigate = useNavigate();

  // Redirect if already logged in
  React.useEffect(() => {
    if (isAuthenticated) {
      navigate(role === "lab" ? "/lab/add-patient" : "/doctor/search");
    }
  }, [isAuthenticated, navigate, role]);

  const handleSubmit = (e) => {
    e.preventDefault();
    login(username, password, role);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-50 to-white py-12 px-4 sm:px-6 lg:px-8">
      <div className="absolute top-0 left-0 w-full h-32 bg-blue-600 z-0"></div>
      
      <div className="max-w-md w-full bg-white rounded-xl shadow-xl p-8 z-10 relative">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-extrabold text-blue-600 mb-2">MedLab System</h1>
          <div className="h-1 w-16 bg-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">
            Sign in to access the medical laboratory system
          </p>
        </div>

        <form className="space-y-6" onSubmit={handleSubmit}>
          <div className="space-y-4">
            <Input
              id="username"
              label="Username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              required
            />

            <Input
              id="password"
              label="Password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />

            <div className="mb-4">
              <label className="block mb-2 font-medium text-gray-700">
                Select Your Role
              </label>
              <div className="grid grid-cols-2 gap-3">
                <div 
                  className={`border rounded-md px-4 py-3 cursor-pointer transition-colors ${
                    role === "lab" 
                      ? "bg-blue-50 border-blue-500 text-blue-700" 
                      : "border-gray-300 hover:bg-gray-50"
                  }`}
                  onClick={() => setRole("lab")}
                >
                  <input
                    type="radio"
                    name="role"
                    value="lab"
                    checked={role === "lab"}
                    onChange={() => setRole("lab")}
                    className="h-4 w-4 text-blue-600 border-gray-300 focus:ring-blue-500"
                  />
                  <span className="ml-2 font-medium">Laboratory</span>
                </div>
                
                <div 
                  className={`border rounded-md px-4 py-3 cursor-pointer transition-colors ${
                    role === "doctor" 
                      ? "bg-blue-50 border-blue-500 text-blue-700" 
                      : "border-gray-300 hover:bg-gray-50"
                  }`}
                  onClick={() => setRole("doctor")}
                >
                  <input
                    type="radio"
                    name="role"
                    value="doctor"
                    checked={role === "doctor"}
                    onChange={() => setRole("doctor")}
                    className="h-4 w-4 text-blue-600 border-gray-300 focus:ring-blue-500"
                  />
                  <span className="ml-2 font-medium">Doctor</span>
                </div>
              </div>
            </div>
          </div>

          {error && (
            <div className="bg-red-50 text-red-600 p-4 rounded-md text-sm border border-red-200">
              <div className="flex items-center">
                <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                  <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd"></path>
                </svg>
                {error}
              </div>
            </div>
          )}

          <div>
            <Button 
              type="submit" 
              variant="primary" 
              fullWidth 
              disabled={loading}
              className="py-3"
            >
              {loading ? (
                <div className="flex items-center justify-center">
                  <Spinner size="small" color="white" />
                  <span className="ml-2">Signing in...</span>
                </div>
              ) : (
                "Sign in"
              )}
            </Button>
          </div>

          <div className="text-center text-sm text-gray-500 border-t border-gray-200 pt-4 mt-6">
            For demo: use any username and password
          </div>
        </form>
      </div>
      
      <div className="mt-8 text-center text-sm text-gray-500">
        &copy; {new Date().getFullYear()} MedLab System. All rights reserved.
      </div>
    </div>
  );
};

export default Login;
