import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Layout from "../../components/Layout";
import Input from "../../components/Input";
import Button from "../../components/Button";
import Spinner from "../../components/Spinner";
import { usePatients } from "../../context/PatientContext";

const AddPatient = () => {
  // Form state
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    id: "",
    results: "",
  });
  const [image, setImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [formErrors, setFormErrors] = useState({});
  const [success, setSuccess] = useState(false);

  // Responsive state
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const isMobile = windowWidth < 768;
  const isTablet = windowWidth >= 768 && windowWidth < 1024;

  const { addPatient, loading } = usePatients();
  const navigate = useNavigate();

  // Handle window resize for responsiveness
  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Input change handler
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));

    // Clear error when field is edited
    if (formErrors[name]) {
      setFormErrors((prevErrors) => ({
        ...prevErrors,
        [name]: null,
      }));
    }
  };

  // Image change handler
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Check file type and size
    if (!file.type.match("image.*")) {
      setFormErrors({
        ...formErrors,
        image: "Please select an image file",
      });
      return;
    }

    if (file.size > 5 * 1024 * 1024) {
      // 5MB limit
      setFormErrors({
        ...formErrors,
        image: "Image must be less than 5MB",
      });
      return;
    }

    setImage(file);
    setFormErrors({
      ...formErrors,
      image: null,
    });

    // Create preview URL
    const reader = new FileReader();
    reader.onloadend = () => {
      setPreviewUrl(reader.result);
    };
    reader.readAsDataURL(file);
  };

  // Form validation
  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) {
      errors.name = "Name is required";
    }

    if (!formData.age) {
      errors.age = "Age is required";
    } else if (isNaN(formData.age) || formData.age <= 0) {
      errors.age = "Age must be a positive number";
    }

    if (!formData.id.trim()) {
      errors.id = "ID is required";
    }

    if (!image) {
      errors.image = "Patient image is required";
    }

    return errors;
  };

  // Form submission handler
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    try {
      // In a real app, we would upload the image to a server
      // For now, we'll just use the local URL
      const patientData = {
        ...formData,
        age: parseInt(formData.age),
        image: previewUrl,
      };

      await addPatient(patientData);
      setSuccess(true);

      // Reset form after 2 seconds
      setTimeout(() => {
        setFormData({
          name: "",
          age: "",
          id: "",
          results: "",
        });
        setImage(null);
        setPreviewUrl(null);
        setSuccess(false);
      }, 2000);
    } catch (err) {
      console.error("Error adding patient:", err);
    }
  };

  // ===== STYLES =====

  // Page container style
  const pageStyle = {
    padding: isMobile ? "16px" : isTablet ? "24px" : "32px",
    maxWidth: "1200px",
    margin: "0 auto",
  };

  // Header section style
  const headerStyle = {
    marginBottom: isMobile ? "20px" : "32px",
    borderLeft: "4px solid #3b82f6",
    paddingLeft: "12px",
  };

  const pageTitleStyle = {
    fontSize: isMobile ? "20px" : "24px",
    fontWeight: "600",
    color: "#1e3a8a",
    margin: 0,
    marginBottom: "8px",
  };

  const pageDescStyle = {
    fontSize: isMobile ? "14px" : "16px",
    color: "#64748b",
    margin: 0,
  };

  // Success message style
  const successMsgStyle = {
    display: "flex",
    alignItems: "center",
    backgroundColor: "#dcfce7",
    borderLeft: "4px solid #22c55e",
    color: "#166534",
    padding: "12px 16px",
    borderRadius: "4px",
    marginBottom: "24px",
    fontSize: isMobile ? "14px" : "16px",
    fontWeight: "500",
  };

  // Form container style
  const formContainerStyle = {
    backgroundColor: "#ffffff",
    borderRadius: "8px",
    boxShadow:
      "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06)",
    overflow: "hidden",
  };

  // Form wrapper style
  const formWrapperStyle = {
    padding: isMobile ? "16px" : isTablet ? "24px" : "32px",
  };

  // Section style
  const sectionStyle = {
    marginBottom: "32px",
  };

  const sectionTitleStyle = {
    fontSize: isMobile ? "16px" : "18px",
    fontWeight: "600",
    color: "#1e40af",
    marginBottom: "16px",
    paddingBottom: "8px",
    borderBottom: "1px solid #e2e8f0",
  };

  // Field row style - for responsive layout of form fields
  const fieldRowStyle = {
    marginBottom: "20px",
    width: "100%",
  };

  // Label style
  const labelStyle = {
    display: "block",
    fontSize: isMobile ? "14px" : "15px",
    fontWeight: "500",
    color: "#374151",
    marginBottom: "6px",
  };

  // Simple input style (for direct styled inputs rather than component)
  const textareaStyle = {
    width: "100%",
    padding: "10px 14px",
    borderRadius: "6px",
    border: "1px solid #d1d5db",
    fontSize: isMobile ? "14px" : "15px",
    minHeight: isMobile ? "100px" : "150px",
    resize: "vertical",
    outline: "none",
    boxSizing: "border-box",
  };

  // Required indicator style
  const requiredStyle = {
    color: "#ef4444",
    marginLeft: "2px",
  };

  // Image upload area style
  const uploadAreaStyle = {
    border: "2px dashed #cbd5e1",
    borderRadius: "6px",
    backgroundColor: "#f8fafc",
    padding: isMobile ? "16px" : "24px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    textAlign: "center",
    cursor: "pointer",
  };

  // Icon style
  const iconStyle = {
    width: isMobile ? "40px" : "48px",
    height: isMobile ? "40px" : "48px",
    color: "#64748b",
    marginBottom: "16px",
  };

  // Error message style
  const errorStyle = {
    fontSize: "13px",
    color: "#ef4444",
    marginTop: "4px",
    fontWeight: "500",
  };

  // Preview image container
  const previewContainerStyle = {
    position: "relative",
    width: "100%",
    marginBottom: "16px",
  };

  // Preview image style
  const previewImageStyle = {
    width: "100%",
    maxHeight: "300px",
    objectFit: "cover",
    borderRadius: "6px",
    boxShadow: "0 1px 3px rgba(0,0,0,0.12)",
  };

  // Remove button container style
  const removeButtonContainerStyle = {
    position: "absolute",
    top: "8px",
    right: "8px",
  };

  // Notes box style
  const notesBoxStyle = {
    backgroundColor: "#f0f9ff",
    border: "1px solid #bae6fd",
    borderRadius: "6px",
    padding: isMobile ? "12px 16px" : "16px 20px",
    marginTop: "24px",
  };

  const notesHeaderStyle = {
    fontSize: isMobile ? "14px" : "15px",
    fontWeight: "600",
    color: "#0369a1",
    marginBottom: "8px",
  };

  const notesList = {
    listStyle: "disc",
    paddingLeft: "20px",
    margin: 0,
    fontSize: isMobile ? "13px" : "14px",
    color: "#0284c7",
  };

  const noteItem = {
    marginBottom: "4px",
  };

  // Form footer style
  const formFooterStyle = {
    display: "flex",
    justifyContent: isMobile ? "center" : "flex-end",
    alignItems: "center",
    paddingTop: "24px",
    borderTop: "1px solid #e2e8f0",
    flexDirection: isMobile ? "column" : "row",
    gap: isMobile ? "16px" : "12px",
  };

  // Cancel link style
  const cancelStyle = {
    color: "#6b7280",
    textDecoration: "none",
    fontSize: isMobile ? "14px" : "15px",
    cursor: "pointer",
    order: isMobile ? 2 : 1,
    textAlign: "center",
    padding: isMobile ? "12px 0" : "0",
    width: isMobile ? "100%" : "auto",
  };

  // Submit button wrapper to ensure consistent width on mobile
  const buttonWrapperStyle = {
    width: isMobile ? "100%" : "auto",
    order: isMobile ? 1 : 2,
  };

  return (
    <Layout>
      <div style={pageStyle}>
        {/* Page Header */}
        <div style={headerStyle}>
          <h1 style={pageTitleStyle}>Add New Patient</h1>
          <p style={pageDescStyle}>
            Enter patient information and test results
          </p>
        </div>

        {/* Success Message */}
        {success && (
          <div style={successMsgStyle}>
            <svg
              width="20"
              height="20"
              viewBox="0 0 20 20"
              fill="currentColor"
              style={{ marginRight: "12px" }}
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                fillRule="evenodd"
                d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z"
                clipRule="evenodd"
              />
            </svg>
            Patient added successfully!
          </div>
        )}

        {/* Main Form */}
        <div style={formContainerStyle}>
          <form onSubmit={handleSubmit}>
            <div style={formWrapperStyle}>
              {/* Form layout changes based on screen size */}
              <div
                style={{
                  display: "grid",
                  gridTemplateColumns: isMobile || isTablet ? "1fr" : "1fr 1fr",
                  gap: isMobile ? "24px" : "32px",
                }}
              >
                {/* Personal Information Section */}
                <div style={sectionStyle}>
                  <h2 style={sectionTitleStyle}>Personal Information</h2>

                  <div style={fieldRowStyle}>
                    <label style={labelStyle} htmlFor="name">
                      Patient Name <span style={requiredStyle}>*</span>
                    </label>
                    <Input
                      id="name"
                      name="name"
                      type="text"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Enter full name"
                      required={true}
                      error={formErrors.name}
                      fullWidth={true}
                    />
                  </div>

                  <div style={fieldRowStyle}>
                    <label style={labelStyle} htmlFor="id">
                      Patient ID <span style={requiredStyle}>*</span>
                    </label>
                    <Input
                      id="id"
                      name="id"
                      type="text"
                      value={formData.id}
                      onChange={handleInputChange}
                      placeholder="Enter unique identifier"
                      required={true}
                      error={formErrors.id}
                      fullWidth={true}
                    />
                  </div>

                  <div style={fieldRowStyle}>
                    <label style={labelStyle} htmlFor="age">
                      Patient Age <span style={requiredStyle}>*</span>
                    </label>
                    <Input
                      id="age"
                      name="age"
                      type="number"
                      value={formData.age}
                      onChange={handleInputChange}
                      placeholder="Enter age in years"
                      required={true}
                      error={formErrors.age}
                      fullWidth={true}
                    />
                  </div>

                  <div style={fieldRowStyle}>
                    <label style={labelStyle} htmlFor="results">
                      Test Results
                    </label>
                    <textarea
                      id="results"
                      name="results"
                      value={formData.results}
                      onChange={handleInputChange}
                      placeholder="Enter detailed test results"
                      style={textareaStyle}
                      onFocus={(e) => {
                        e.target.style.borderColor = "#3b82f6";
                        e.target.style.boxShadow =
                          "0 0 0 3px rgba(59, 130, 246, 0.1)";
                      }}
                      onBlur={(e) => {
                        e.target.style.borderColor = "#d1d5db";
                        e.target.style.boxShadow = "none";
                      }}
                    />
                  </div>
                </div>

                {/* Patient Image Section */}
                <div style={sectionStyle}>
                  <h2 style={sectionTitleStyle}>Patient Image</h2>

                  {/* Image Preview */}
                  {previewUrl ? (
                    <div style={previewContainerStyle}>
                      <img
                        src={previewUrl}
                        alt="Patient"
                        style={previewImageStyle}
                      />
                      <div style={removeButtonContainerStyle}>
                        <Button
                          size="small"
                          variant="danger"
                          onClick={() => {
                            setImage(null);
                            setPreviewUrl(null);
                          }}
                        >
                          Remove
                        </Button>
                      </div>
                    </div>
                  ) : (
                    <div style={{ marginBottom: "16px" }}>
                      <label style={labelStyle}>
                        Patient Image <span style={requiredStyle}>*</span>
                      </label>
                      <label htmlFor="image-upload" style={uploadAreaStyle}>
                        <svg
                          style={iconStyle}
                          stroke="currentColor"
                          fill="none"
                          viewBox="0 0 48 48"
                        >
                          <path
                            d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02"
                            strokeWidth={2}
                            strokeLinecap="round"
                            strokeLinejoin="round"
                          />
                        </svg>
                        <div style={{ marginBottom: "12px" }}>
                          <span
                            style={{
                              display: "inline-block",
                              padding: "8px 16px",
                              backgroundColor: "#eff6ff",
                              color: "#2563eb",
                              fontWeight: "500",
                              borderRadius: "4px",
                              marginBottom: "8px",
                            }}
                          >
                            Select image
                          </span>
                          <p
                            style={{
                              margin: "0",
                              fontSize: isMobile ? "13px" : "14px",
                              color: "#6b7280",
                            }}
                          >
                            or drag and drop
                          </p>
                        </div>
                        <p
                          style={{
                            margin: "0",
                            fontSize: "12px",
                            color: "#94a3b8",
                          }}
                        >
                          PNG, JPG, GIF up to 5MB
                        </p>
                        <input
                          id="image-upload"
                          name="image-upload"
                          type="file"
                          style={{ display: "none" }}
                          accept="image/*"
                          onChange={handleImageChange}
                        />
                      </label>
                      {formErrors.image && (
                        <p style={errorStyle}>{formErrors.image}</p>
                      )}
                    </div>
                  )}

                  {/* Notes Box */}
                  <div style={notesBoxStyle}>
                    <h3 style={notesHeaderStyle}>Important Guidelines</h3>
                    <ul style={notesList}>
                      <li style={noteItem}>
                        All images must be clear and well-lit
                      </li>
                      <li style={noteItem}>
                        Patient ID must match hospital records
                      </li>
                      <li style={noteItem}>
                        Verify all information before submission
                      </li>
                      {!isMobile && (
                        <li style={noteItem}>
                          Include reference ranges where applicable
                        </li>
                      )}
                    </ul>
                  </div>
                </div>
              </div>

              {/* Form Footer */}
              <div style={formFooterStyle}>
                <span style={cancelStyle} onClick={() => navigate(-1)}>
                  Cancel
                </span>
                <div style={buttonWrapperStyle}>
                  <Button
                    type="submit"
                    variant="primary"
                    disabled={loading}
                    style={{
                      width: isMobile ? "100%" : "auto",
                      minWidth: isMobile ? "auto" : "160px",
                    }}
                  >
                    {loading ? (
                      <div
                        style={{
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                        }}
                      >
                        <Spinner size="small" color="white" />
                        <span style={{ marginLeft: "8px" }}>Saving...</span>
                      </div>
                    ) : (
                      "Add Patient"
                    )}
                  </Button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
    </Layout>
  );
};

export default AddPatient;
