import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Layout from "../../components/Layout";
import Input from "../../components/Input";
import Button from "../../components/Button";
import Spinner from "../../components/Spinner";
import { usePatients } from "../../context/PatientContext";

const AddPatient = () => {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    id: "",
    results: "",
  });
  const [image, setImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [formErrors, setFormErrors] = useState({});
  const [success, setSuccess] = useState(false);

  const { addPatient, loading } = usePatients();
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    console.log(`Input changed: ${name} = ${value}`);

    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));

    // Clear error when field is edited
    if (formErrors[name]) {
      setFormErrors((prevErrors) => ({
        ...prevErrors,
        [name]: null,
      }));
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Check file type and size
    if (!file.type.match("image.*")) {
      setFormErrors({
        ...formErrors,
        image: "Please select an image file",
      });
      return;
    }

    if (file.size > 5 * 1024 * 1024) {
      // 5MB limit
      setFormErrors({
        ...formErrors,
        image: "Image must be less than 5MB",
      });
      return;
    }

    setImage(file);
    setFormErrors({
      ...formErrors,
      image: null,
    });

    // Create preview URL
    const reader = new FileReader();
    reader.onloadend = () => {
      setPreviewUrl(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) {
      errors.name = "Name is required";
    }

    if (!formData.age) {
      errors.age = "Age is required";
    } else if (isNaN(formData.age) || formData.age <= 0) {
      errors.age = "Age must be a positive number";
    }

    if (!formData.id.trim()) {
      errors.id = "ID is required";
    }

    if (!image) {
      errors.image = "Patient image is required";
    }

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form submitted with data:", formData);

    // Validate form
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    try {
      // In a real app, we would upload the image to a server
      // For now, we'll just use the local URL
      const patientData = {
        ...formData,
        age: parseInt(formData.age),
        image: previewUrl,
      };

      await addPatient(patientData);
      setSuccess(true);

      // Reset form after 2 seconds
      setTimeout(() => {
        setFormData({
          name: "",
          age: "",
          id: "",
          results: "",
        });
        setImage(null);
        setPreviewUrl(null);
        setSuccess(false);
      }, 2000);
    } catch (err) {
      console.error("Error adding patient:", err);
    }
  };

  return (
    <Layout>
      <div className="max-w-5xl mx-auto px-4 py-8">
        <div className="mb-8 flex flex-col sm:flex-row sm:items-center sm:justify-between">
          <div>
            <h1 className="text-2xl font-bold text-gray-800 border-l-4 border-blue-600 pl-3">
              Add New Patient
            </h1>
            <p className="text-gray-600 mt-2">
              Enter patient information and test results
            </p>
          </div>
          <Button
            variant="secondary"
            onClick={() => navigate(-1)}
            className="mt-4 sm:mt-0"
            icon={
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-5 w-5"
                viewBox="0 0 20 20"
                fill="currentColor"
              >
                <path
                  fillRule="evenodd"
                  d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z"
                  clipRule="evenodd"
                />
              </svg>
            }
          >
            Back to Patients
          </Button>
        </div>

        {success && (
          <div className="mb-8 p-4 rounded-md bg-green-50 text-green-700 border border-green-200 flex items-center shadow-sm">
            <svg
              className="w-5 h-5 mr-3"
              fill="currentColor"
              viewBox="0 0 20 20"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                fillRule="evenodd"
                d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z"
                clipRule="evenodd"
              ></path>
            </svg>
            <span className="font-medium">
              Patient information saved successfully!
            </span>
          </div>
        )}

        <div className="bg-white p-6 md:p-8 rounded-lg shadow-md border border-gray-100">
          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div className="space-y-6">
                <h2 className="text-lg font-semibold text-gray-800 pb-2 border-b border-gray-200">
                  Personal Information
                </h2>

                <Input
                  label="Patient Name"
                  id="name"
                  name="name"
                  type="text"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="Full name"
                  required={true}
                  error={formErrors.name}
                />

                <div className="grid grid-cols-2 gap-6">
                  <Input
                    label="Age"
                    id="age"
                    name="age"
                    type="number"
                    value={formData.age}
                    onChange={handleInputChange}
                    placeholder="Age in years"
                    required={true}
                    error={formErrors.age}
                  />

                  <Input
                    label="Patient ID"
                    id="id"
                    name="id"
                    type="text"
                    value={formData.id}
                    onChange={handleInputChange}
                    placeholder="Unique ID"
                    required={true}
                    error={formErrors.id}
                  />
                </div>

                <div className="mt-6">
                  <label
                    className="block mb-2 font-medium text-gray-700"
                    htmlFor="results"
                  >
                    Test Results
                  </label>
                  <textarea
                    id="results"
                    name="results"
                    value={formData.results}
                    onChange={handleInputChange}
                    className="w-full border border-gray-300 rounded-md px-4 py-3 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                    rows="6"
                    placeholder="Enter detailed test results (optional)"
                  />
                </div>
              </div>

              <div className="space-y-6">
                <h2 className="text-lg font-semibold text-gray-800 pb-2 border-b border-gray-200">
                  Patient Image
                </h2>

                <div>
                  <label className="block mb-2 font-medium text-gray-700">
                    Upload Image <span className="text-red-500">*</span>
                  </label>

                  <div className="mt-2 border-2 border-gray-300 border-dashed rounded-lg overflow-hidden bg-gray-50">
                    {previewUrl ? (
                      <div className="relative">
                        <img
                          src={previewUrl}
                          alt="Patient"
                          className="w-full h-auto max-h-96 object-cover"
                        />
                        <div className="absolute top-2 right-2">
                          <Button
                            size="small"
                            variant="danger"
                            className="bg-white bg-opacity-90 hover:bg-opacity-100 shadow-sm"
                            onClick={() => {
                              setImage(null);
                              setPreviewUrl(null);
                            }}
                          >
                            Remove
                          </Button>
                        </div>
                      </div>
                    ) : (
                      <div className="space-y-3 text-center p-10">
                        <svg
                          className="mx-auto h-14 w-14 text-gray-400"
                          stroke="currentColor"
                          fill="none"
                          viewBox="0 0 48 48"
                        >
                          <path
                            d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02"
                            strokeWidth={2}
                            strokeLinecap="round"
                            strokeLinejoin="round"
                          />
                        </svg>
                        <div className="flex flex-col items-center text-sm text-gray-600">
                          <label
                            htmlFor="image-upload"
                            className="relative cursor-pointer bg-blue-50 rounded-md py-2 px-4 font-medium text-blue-600 hover:text-blue-500 focus-within:outline-none mb-2"
                          >
                            <span>Select file</span>
                            <input
                              id="image-upload"
                              name="image-upload"
                              type="file"
                              className="sr-only"
                              accept="image/*"
                              onChange={handleImageChange}
                            />
                          </label>
                          <p className="text-gray-500">
                            or drag and drop image
                          </p>
                        </div>
                        <p className="text-xs text-gray-500">
                          Supported formats: PNG, JPG, GIF (max 5MB)
                        </p>
                      </div>
                    )}
                  </div>

                  {formErrors.image && (
                    <p className="mt-2 text-sm text-red-600 font-medium">
                      {formErrors.image}
                    </p>
                  )}
                </div>

                <div className="mt-6 p-4 bg-blue-50 rounded-lg border border-blue-100">
                  <h3 className="text-sm font-medium text-blue-800 mb-2">
                    Important Notes
                  </h3>
                  <ul className="text-sm text-blue-700 space-y-1 list-disc pl-5">
                    <li>All images must be clear and properly lit</li>
                    <li>Patient ID must match hospital records</li>
                    <li>Verify all information before submission</li>
                  </ul>
                </div>
              </div>
            </div>

            <div className="mt-10 pt-6 border-t border-gray-200 flex flex-wrap justify-end gap-4">
              <Button
                type="button"
                variant="secondary"
                onClick={() => navigate(-1)}
                disabled={loading}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                variant="primary"
                disabled={loading}
                className="min-w-[140px]"
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <Spinner size="small" color="white" />
                    <span className="ml-2">Saving...</span>
                  </div>
                ) : (
                  "Save Patient"
                )}
              </Button>
            </div>
          </form>
        </div>
      </div>
    </Layout>
  );
};

export default AddPatient;
