import React, { createContext, useContext, useState, useEffect } from "react";

// Create the auth context
const AuthContext = createContext();

// Auth provider component
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tokens, setTokens] = useState({
    access: localStorage.getItem("accessToken"),
    refresh: localStorage.getItem("refreshToken"),
  });

  // Check if user is already logged in
  useEffect(() => {
    const accessToken = localStorage.getItem("accessToken");
    const refreshToken = localStorage.getItem("refreshToken");
    const userRole = localStorage.getItem("userRole");
    const userEmail = localStorage.getItem("userEmail");

    if (accessToken && refreshToken && userRole && userEmail) {
      setUser({
        email: userEmail,
        role: userRole.toLowerCase(),
      });
      setTokens({
        access: accessToken,
        refresh: refreshToken,
      });
    }
  }, []);

  // Login function - connects to API
  const login = async (email, password) => {
    setLoading(true);
    setError(null);

    console.log("Login attempt with:", email);

    try {
      console.log(
        "Sending API request to:",
        "http://localhost:8000/api/auth/login/"
      );
      const response = await fetch("http://localhost:8000/api/auth/login/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      console.log("API response status:", response.status);
      const data = await response.json();
      console.log("API response data:", data);

      if (response.ok) {
        console.log("Login successful, storing tokens and user data");

        // Ensure role is lowercase for consistent comparison
        const userRole = data.user.role.toLowerCase();
        console.log("Normalized user role:", userRole);

        // Store tokens in localStorage
        localStorage.setItem("accessToken", data.access);
        localStorage.setItem("refreshToken", data.refresh);
        localStorage.setItem("userRole", userRole);
        localStorage.setItem("userEmail", data.user.email);

        console.log("Setting tokens in state");
        setTokens({
          access: data.access,
          refresh: data.refresh,
        });

        console.log("Setting user in state, role:", userRole);
        setUser({
          email: data.user.email,
          role: userRole,
        });

        setLoading(false);
        return { success: true };
      } else {
        console.log("Login failed:", data.detail || "Unknown error");
        setError(data.detail || "Login failed");
        setLoading(false);
        return { success: false, error: data.detail };
      }
    } catch (err) {
      console.error("Network error during login:", err);
      setError("Network error or server unavailable");
      setLoading(false);
      return { success: false, error: "Network error" };
    }
  };

  // Refresh token function
  const refreshToken = async () => {
    try {
      const response = await fetch(
        "http://localhost:8000/api/auth/token/refresh/",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ refresh: tokens.refresh }),
        }
      );

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem("accessToken", data.access);
        setTokens({
          ...tokens,
          access: data.access,
        });
        return true;
      } else {
        // If refresh token is invalid, log out the user
        logout();
        return false;
      }
    } catch (err) {
      console.error("Error refreshing token:", err);
      return false;
    }
  };

  // Logout function
  const logout = () => {
    localStorage.removeItem("accessToken");
    localStorage.removeItem("refreshToken");
    localStorage.removeItem("userRole");
    localStorage.removeItem("userEmail");
    setUser(null);
    setTokens({ access: null, refresh: null });
  };

  const value = {
    user,
    loading,
    error,
    login,
    logout,
    refreshToken,
    tokens,
    isAuthenticated: !!user,
    isLab: user?.role === "lab",
    isDoctor: user?.role === "doctor",
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Custom hook for using auth context
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};
