import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Layout from "../../components/Layout";
import Input from "../../components/Input";
import Button from "../../components/Button";
import Spinner from "../../components/Spinner";
import { usePatients } from "../../context/PatientContext";

const AddPatient = () => {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    id: "",
    results: "",
  });
  const [image, setImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [formErrors, setFormErrors] = useState({});
  const [success, setSuccess] = useState(false);

  const { addPatient, loading } = usePatients();
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    console.log(`Input changed: ${name} = ${value}`);

    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));

    // Clear error when field is edited
    if (formErrors[name]) {
      setFormErrors((prevErrors) => ({
        ...prevErrors,
        [name]: null,
      }));
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Check file type and size
    if (!file.type.match("image.*")) {
      setFormErrors({
        ...formErrors,
        image: "Please select an image file",
      });
      return;
    }

    if (file.size > 5 * 1024 * 1024) {
      // 5MB limit
      setFormErrors({
        ...formErrors,
        image: "Image must be less than 5MB",
      });
      return;
    }

    setImage(file);
    setFormErrors({
      ...formErrors,
      image: null,
    });

    // Create preview URL
    const reader = new FileReader();
    reader.onloadend = () => {
      setPreviewUrl(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) {
      errors.name = "Name is required";
    }

    if (!formData.age) {
      errors.age = "Age is required";
    } else if (isNaN(formData.age) || formData.age <= 0) {
      errors.age = "Age must be a positive number";
    }

    if (!formData.id.trim()) {
      errors.id = "ID is required";
    }

    if (!image) {
      errors.image = "Patient image is required";
    }

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form submitted with data:", formData);

    // Validate form
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    try {
      // In a real app, we would upload the image to a server
      // For now, we'll just use the local URL
      const patientData = {
        ...formData,
        age: parseInt(formData.age),
        image: previewUrl,
      };

      await addPatient(patientData);
      setSuccess(true);

      // Reset form after 2 seconds
      setTimeout(() => {
        setFormData({
          name: "",
          age: "",
          id: "",
          results: "",
        });
        setImage(null);
        setPreviewUrl(null);
        setSuccess(false);
      }, 2000);
    } catch (err) {
      console.error("Error adding patient:", err);
    }
  };

  // Container styles
  const containerStyle = {
    maxWidth: "1100px",
    margin: "0 auto",
    padding: "40px 24px",
  };

  // Header styles
  const headerContainerStyle = {
    display: "flex",
    flexDirection: "column",
    marginBottom: "32px",
  };

  const headerTitleStyle = {
    borderLeft: "4px solid #2563eb",
    paddingLeft: "12px",
  };

  const titleStyle = {
    fontSize: "28px",
    fontWeight: "600",
    color: "#1e293b",
    margin: "0",
    marginBottom: "6px",
  };

  const subtitleStyle = {
    fontSize: "16px",
    color: "#64748b",
    marginTop: "8px",
  };

  // Success message styles
  const successMessageStyle = {
    display: "flex",
    alignItems: "center",
    backgroundColor: "#ecfdf5",
    borderRadius: "8px",
    border: "1px solid #a7f3d0",
    color: "#047857",
    padding: "16px",
    marginBottom: "24px",
    boxShadow: "0 1px 2px rgba(0, 0, 0, 0.05)",
  };

  const successTextStyle = {
    fontWeight: "500",
    marginLeft: "12px",
  };

  // Form container styles
  const formContainerStyle = {
    backgroundColor: "white",
    borderRadius: "12px",
    boxShadow:
      "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)",
    border: "1px solid #f1f5f9",
    padding: "36px",
  };

  // Improved section styles
  const sectionStyle = {
    display: "flex",
    flexDirection: "column",
    gap: "32px",
  };

  const sectionHeaderStyle = {
    fontSize: "18px",
    fontWeight: "600",
    color: "#1e293b",
    paddingBottom: "12px",
    borderBottom: "1px solid #e2e8f0",
    marginBottom: "16px",
  };

  // Updated grid layout style
  const formGridStyle = {
    display: "grid",
    gridTemplateColumns: "1fr",
    gap: "48px",
  };

  // Form field container
  const fieldContainerStyle = {
    marginBottom: "24px",
  };

  // Two column grid for age and ID
  const twoColumnGridStyle = {
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    gap: "24px",
  };

  // Image upload area styles
  const imageUploadContainerStyle = {
    border: "2px dashed #cbd5e1",
    borderRadius: "8px",
    overflow: "hidden",
    backgroundColor: "#f8fafc",
  };

  const dropzoneStyle = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    textAlign: "center",
    padding: "40px 20px",
  };

  const uploadIconStyle = {
    width: "56px",
    height: "56px",
    color: "#94a3b8",
    marginBottom: "16px",
  };

  const imagePreviewStyle = {
    position: "relative",
    width: "100%",
  };

  const previewImageStyle = {
    width: "100%",
    height: "auto",
    maxHeight: "400px",
    objectFit: "cover",
    display: "block",
  };

  const removeButtonContainerStyle = {
    position: "absolute",
    top: "8px",
    right: "8px",
  };

  // Notes section styles
  const notesContainerStyle = {
    backgroundColor: "#eff6ff",
    borderRadius: "8px",
    border: "1px solid #bfdbfe",
    padding: "16px",
    marginTop: "24px",
  };

  const notesHeaderStyle = {
    fontSize: "15px",
    fontWeight: "600",
    color: "#1e40af",
    marginBottom: "8px",
  };

  const notesListStyle = {
    listStyleType: "disc",
    paddingLeft: "20px",
    color: "#2563eb",
    fontSize: "14px",
  };

  const noteItemStyle = {
    marginBottom: "4px",
  };

  // Form footer styles
  const formFooterStyle = {
    display: "flex",
    justifyContent: "flex-end",
    alignItems: "center",
    gap: "16px",
    marginTop: "48px",
    paddingTop: "24px",
    borderTop: "1px solid #e2e8f0",
  };

  const cancelLinkStyle = {
    color: "#64748b",
    fontSize: "15px",
    cursor: "pointer",
    marginRight: "8px",
    textDecoration: "none",
    fontWeight: "500",
  };

  // Textarea styles
  const textareaStyle = {
    width: "100%",
    padding: "12px 16px",
    borderRadius: "8px",
    border: "1px solid #cbd5e1",
    fontSize: "16px",
    lineHeight: "1.5",
    backgroundColor: "#ffffff",
    color: "#0f172a",
    transition: "all 0.2s ease",
    outline: "none",
    resize: "vertical",
    minHeight: "150px",
  };

  const textareaFocusStyle = {
    borderColor: "#3b82f6",
    boxShadow: "0 0 0 3px rgba(59, 130, 246, 0.3)",
  };

  const labelStyle = {
    display: "block",
    marginBottom: "8px",
    fontWeight: "500",
    fontSize: "15px",
    color: "#334155",
  };

  const requiredStyle = {
    color: "#ef4444",
    marginLeft: "3px",
  };

  return (
    <Layout>
      <div style={containerStyle}>
        <div style={headerContainerStyle}>
          <div style={headerTitleStyle}>
            <h1 style={titleStyle}>Add New Patient</h1>
            <p style={subtitleStyle}>
              Enter complete patient information and test results
            </p>
          </div>
        </div>

        {success && (
          <div style={successMessageStyle}>
            <svg
              width="24"
              height="24"
              fill="currentColor"
              viewBox="0 0 20 20"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                fillRule="evenodd"
                d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z"
                clipRule="evenodd"
              ></path>
            </svg>
            <span style={successTextStyle}>
              Patient information saved successfully!
            </span>
          </div>
        )}

        <div
          style={{
            ...formContainerStyle,
            padding: "40px",
            boxShadow:
              "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)",
          }}
        >
          <form onSubmit={handleSubmit}>
            <div
              style={{
                ...formGridStyle,
                gridTemplateColumns:
                  window.innerWidth > 768 ? "1fr 1fr" : "1fr",
              }}
            >
              {/* Left Column - Personal Information */}
              <div style={sectionStyle}>
                <h2
                  style={{
                    ...sectionHeaderStyle,
                    color: "#1e40af",
                    borderBottomColor: "#bfdbfe",
                  }}
                >
                  Personal Information
                </h2>

                <div style={fieldContainerStyle}>
                  <Input
                    label="Patient Name"
                    id="name"
                    name="name"
                    type="text"
                    value={formData.name}
                    onChange={handleInputChange}
                    placeholder="Enter patient's full name"
                    required={true}
                    error={formErrors.name}
                  />
                </div>

                <div style={fieldContainerStyle}>
                  <Input
                    label="Patient ID"
                    id="id"
                    name="id"
                    type="text"
                    value={formData.id}
                    onChange={handleInputChange}
                    placeholder="Unique identifier"
                    required={true}
                    error={formErrors.id}
                  />
                </div>

                <div style={fieldContainerStyle}>
                  <Input
                    label="Age"
                    id="age"
                    name="age"
                    type="number"
                    value={formData.age}
                    onChange={handleInputChange}
                    placeholder="Years"
                    required={true}
                    error={formErrors.age}
                  />
                </div>

                <div>
                  <label
                    style={{
                      ...labelStyle,
                      fontSize: "16px",
                      marginBottom: "10px",
                    }}
                    htmlFor="results"
                  >
                    Test Results
                  </label>
                  <textarea
                    id="results"
                    name="results"
                    value={formData.results}
                    onChange={handleInputChange}
                    style={{
                      ...textareaStyle,
                      minHeight: "180px",
                      borderRadius: "10px",
                    }}
                    placeholder="Enter detailed test results and observations"
                    onFocus={(e) =>
                      Object.assign(e.target.style, textareaFocusStyle)
                    }
                    onBlur={(e) => {
                      e.target.style.borderColor = "#cbd5e1";
                      e.target.style.boxShadow = "none";
                    }}
                  />
                </div>
              </div>

              {/* Right Column - Patient Image */}
              <div style={sectionStyle}>
                <h2
                  style={{
                    ...sectionHeaderStyle,
                    color: "#1e40af",
                    borderBottomColor: "#bfdbfe",
                  }}
                >
                  Patient Image
                </h2>

                <div>
                  <label
                    style={{
                      ...labelStyle,
                      fontSize: "16px",
                      marginBottom: "10px",
                    }}
                  >
                    Upload Image <span style={requiredStyle}>*</span>
                  </label>

                  <div
                    style={{
                      ...imageUploadContainerStyle,
                      borderColor: "#bfdbfe",
                      marginTop: "8px",
                      borderRadius: "10px",
                    }}
                  >
                    {previewUrl ? (
                      <div style={imagePreviewStyle}>
                        <img
                          src={previewUrl}
                          alt="Patient"
                          style={{
                            ...previewImageStyle,
                            borderRadius: "6px",
                          }}
                        />
                        <div style={removeButtonContainerStyle}>
                          <Button
                            size="small"
                            variant="danger"
                            onClick={() => {
                              setImage(null);
                              setPreviewUrl(null);
                            }}
                          >
                            Remove
                          </Button>
                        </div>
                      </div>
                    ) : (
                      <div
                        style={{
                          ...dropzoneStyle,
                          padding: "50px 20px",
                        }}
                      >
                        <svg
                          style={{
                            ...uploadIconStyle,
                            width: "70px",
                            height: "70px",
                            color: "#60a5fa",
                          }}
                          stroke="currentColor"
                          fill="none"
                          viewBox="0 0 48 48"
                        >
                          <path
                            d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02"
                            strokeWidth={2}
                            strokeLinecap="round"
                            strokeLinejoin="round"
                          />
                        </svg>
                        <div
                          style={{ marginBottom: "20px", marginTop: "16px" }}
                        >
                          <label
                            htmlFor="image-upload"
                            style={{
                              display: "inline-block",
                              padding: "12px 20px",
                              backgroundColor: "#eff6ff",
                              color: "#2563eb",
                              fontWeight: "500",
                              borderRadius: "8px",
                              cursor: "pointer",
                              marginBottom: "16px",
                              transition: "all 0.2s ease",
                              boxShadow: "0 1px 2px rgba(0, 0, 0, 0.05)",
                            }}
                            onMouseOver={(e) => {
                              e.target.style.backgroundColor = "#dbeafe";
                            }}
                            onMouseOut={(e) => {
                              e.target.style.backgroundColor = "#eff6ff";
                            }}
                          >
                            <span>Choose Image File</span>
                            <input
                              id="image-upload"
                              name="image-upload"
                              type="file"
                              style={{ display: "none" }}
                              accept="image/*"
                              onChange={handleImageChange}
                            />
                          </label>
                          <p style={{ color: "#4b5563", fontSize: "14px" }}>
                            or drag and drop image here
                          </p>
                        </div>
                        <p style={{ color: "#6b7280", fontSize: "13px" }}>
                          Supported formats: PNG, JPG, GIF (maximum 5MB)
                        </p>
                      </div>
                    )}
                  </div>

                  {formErrors.image && (
                    <p
                      style={{
                        marginTop: "10px",
                        fontSize: "14px",
                        fontWeight: "500",
                        color: "#ef4444",
                      }}
                    >
                      {formErrors.image}
                    </p>
                  )}
                </div>

                <div
                  style={{
                    ...notesContainerStyle,
                    backgroundColor: "#f0f9ff",
                    borderColor: "#bae6fd",
                    borderRadius: "10px",
                    padding: "20px",
                  }}
                >
                  <h3
                    style={{
                      ...notesHeaderStyle,
                      color: "#0369a1",
                      fontSize: "16px",
                    }}
                  >
                    Important Guidelines
                  </h3>
                  <ul
                    style={{
                      ...notesListStyle,
                      color: "#0284c7",
                    }}
                  >
                    <li style={noteItemStyle}>
                      All images must be clear and properly lit
                    </li>
                    <li style={noteItemStyle}>
                      Patient ID must match hospital records
                    </li>
                    <li style={noteItemStyle}>
                      Verify all information for accuracy before submission
                    </li>
                    <li style={noteItemStyle}>
                      Test results should include relevant reference ranges
                    </li>
                  </ul>
                </div>
              </div>
            </div>

            <div
              style={{
                ...formFooterStyle,
                borderTopColor: "#e0e7ff",
                marginTop: "56px",
                paddingTop: "28px",
              }}
            >
              <span
                style={{
                  ...cancelLinkStyle,
                  fontSize: "16px",
                  padding: "8px 16px",
                  borderRadius: "6px",
                  transition: "background-color 0.2s ease",
                }}
                onClick={() => navigate(-1)}
                onMouseOver={(e) => {
                  e.target.style.backgroundColor = "#f1f5f9";
                }}
                onMouseOut={(e) => {
                  e.target.style.backgroundColor = "transparent";
                }}
              >
                Cancel
              </span>
              <Button
                type="submit"
                variant="primary"
                disabled={loading}
                style={{
                  minWidth: "180px",
                  height: "52px",
                  fontSize: "16px",
                  borderRadius: "8px",
                }}
              >
                {loading ? (
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                    }}
                  >
                    <Spinner size="small" color="white" />
                    <span style={{ marginLeft: "10px" }}>Saving...</span>
                  </div>
                ) : (
                  "Add Patient"
                )}
              </Button>
            </div>
          </form>
        </div>
      </div>
    </Layout>
  );
};

export default AddPatient;
