import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import Button from "../components/Button";
import Spinner from "../components/Spinner";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("lab");
  const { login, loading, error, isAuthenticated } = useAuth();
  const navigate = useNavigate();

  // Add responsive state
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const isMobile = windowWidth < 768;
  const isTablet = windowWidth >= 768 && windowWidth < 1024;

  // Handle window resize for responsiveness
  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Redirect if already logged in
  useEffect(() => {
    if (isAuthenticated) {
      navigate(role === "lab" ? "/lab/add-patient" : "/doctor/search");
    }
  }, [isAuthenticated, navigate, role]);

  const handleSubmit = (e) => {
    e.preventDefault();
    login(username, password, role);
  };

  // ===== STYLES =====

  // Container styles
  const pageContainerStyle = {
    minHeight: "100vh",
    width: "100%",
    maxWidth: "100%",
    overflowX: "hidden",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    background: "linear-gradient(to bottom, #EFF6FF, #FFFFFF)",
    padding: isMobile ? "16px" : isTablet ? "24px" : "48px",
    position: "relative",
    boxSizing: "border-box",
  };

  // Top header bar style
  const headerBarStyle = {
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: isMobile ? "24px" : "32px",
    backgroundColor: "#2563EB",
    zIndex: 0,
  };

  // Card container style
  const cardContainerStyle = {
    maxWidth: isMobile ? "100%" : isTablet ? "450px" : "480px",
    width: "100%",
    backgroundColor: "#ffffff",
    borderRadius: "12px",
    boxShadow:
      "0 10px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)",
    padding: isMobile ? "20px 16px" : isTablet ? "28px 24px" : "32px",
    zIndex: 10,
    position: "relative",
    boxSizing: "border-box",
  };

  // Title container style
  const titleContainerStyle = {
    textAlign: "center",
    marginBottom: isMobile ? "24px" : "32px",
  };

  // Title style
  const titleStyle = {
    fontSize: isMobile ? "22px" : isTablet ? "26px" : "30px",
    fontWeight: 800,
    color: "#2563EB",
    marginBottom: "8px",
  };

  // Title divider style
  const titleDividerStyle = {
    height: "3px",
    width: isMobile ? "50px" : "60px",
    backgroundColor: "#2563EB",
    margin: "0 auto 16px auto",
  };

  // Subtitle style
  const subtitleStyle = {
    color: "#4B5563",
    fontSize: isMobile ? "13px" : isTablet ? "14px" : "16px",
    padding: "0 8px",
  };

  // Form style
  const formStyle = {
    marginTop: isMobile ? "16px" : "24px",
    width: "100%",
  };

  // Form field group style
  const formGroupStyle = {
    marginBottom: isMobile ? "16px" : "20px",
    width: "100%",
  };

  // Field label style
  const labelStyle = {
    display: "block",
    marginBottom: "6px",
    fontWeight: 500,
    fontSize: isMobile ? "14px" : "15px",
    color: "#374151",
  };

  // Input style
  const inputStyle = {
    width: "100%",
    padding: isMobile ? "10px 12px" : "12px 16px",
    fontSize: isMobile ? "14px" : "15px",
    border: "1px solid #D1D5DB",
    borderRadius: "8px",
    outline: "none",
    boxSizing: "border-box",
    transition: "border-color 0.2s ease, box-shadow 0.2s ease",
  };

  // Input focus style - applies on focus
  const focusStyle = (e) => {
    e.target.style.borderColor = "#3B82F6";
    e.target.style.boxShadow = "0 0 0 3px rgba(59, 130, 246, 0.1)";
  };

  // Input blur style - removes focus styles
  const blurStyle = (e) => {
    e.target.style.borderColor = "#D1D5DB";
    e.target.style.boxShadow = "none";
  };

  // Role selector container
  const roleSelectorContainerStyle = {
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    gap: isMobile ? "8px" : "12px",
    width: "100%",
    boxSizing: "border-box",
  };

  // Role option style - base style for both options
  const getRoleOptionStyle = (isSelected) => ({
    border: `1px solid ${isSelected ? "#3B82F6" : "#D1D5DB"}`,
    borderRadius: "8px",
    padding: isMobile ? "10px 12px" : "12px 16px",
    cursor: "pointer",
    transition: "all 0.2s ease",
    backgroundColor: isSelected ? "#EFF6FF" : "#FFFFFF",
    color: isSelected ? "#2563EB" : "#374151",
    display: "flex",
    alignItems: "center",
    boxSizing: "border-box",
  });

  // Radio input style
  const radioStyle = {
    height: "16px",
    width: "16px",
    accentColor: "#2563EB",
    marginRight: "8px",
    flexShrink: 0,
  };

  // Role label style
  const roleLabelStyle = {
    fontWeight: 500,
    fontSize: isMobile ? "14px" : "15px",
    whiteSpace: "nowrap",
  };

  // Error message container
  const errorContainerStyle = {
    backgroundColor: "#FEF2F2",
    border: "1px solid #FECACA",
    borderRadius: "8px",
    padding: isMobile ? "10px 12px" : "12px 16px",
    marginBottom: isMobile ? "16px" : "20px",
    color: "#DC2626",
    fontSize: isMobile ? "13px" : "14px",
    display: "flex",
    alignItems: "center",
    boxSizing: "border-box",
    width: "100%",
  };

  // Error icon style
  const errorIconStyle = {
    width: isMobile ? "16px" : "20px",
    height: isMobile ? "16px" : "20px",
    marginRight: "8px",
    flexShrink: 0,
  };

  // Button wrapper style
  const buttonWrapperStyle = {
    marginTop: isMobile ? "20px" : "24px",
    width: "100%",
  };

  // Demo text style
  const demoTextStyle = {
    textAlign: "center",
    fontSize: isMobile ? "12px" : "14px",
    color: "#6B7280",
    marginTop: isMobile ? "20px" : "24px",
    paddingTop: isMobile ? "12px" : "16px",
    borderTop: "1px solid #E5E7EB",
    width: "100%",
  };

  // Footer style
  const footerStyle = {
    marginTop: isMobile ? "24px" : isTablet ? "28px" : "32px",
    textAlign: "center",
    fontSize: isMobile ? "12px" : "14px",
    color: "#6B7280",
    width: "100%",
    maxWidth: "100%",
    padding: "0 16px",
    boxSizing: "border-box",
  };

  return (
    <div style={pageContainerStyle}>
      <div style={headerBarStyle}></div>

      <div style={cardContainerStyle}>
        <div style={titleContainerStyle}>
          <h1 style={titleStyle}>MedSec System</h1>
          <div style={titleDividerStyle}></div>
          <p style={subtitleStyle}>
            Sign in to access the medical laboratory system
          </p>
        </div>

        <form style={formStyle} onSubmit={handleSubmit}>
          <div style={formGroupStyle}>
            <label style={labelStyle} htmlFor="username">
              Username
            </label>
            <input
              style={inputStyle}
              id="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              required
              onFocus={focusStyle}
              onBlur={blurStyle}
            />
          </div>

          <div style={formGroupStyle}>
            <label style={labelStyle} htmlFor="password">
              Password
            </label>
            <input
              style={inputStyle}
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
              onFocus={focusStyle}
              onBlur={blurStyle}
            />
          </div>

          <div style={formGroupStyle}>
            <label style={labelStyle}>Select Your Role</label>
            <div style={roleSelectorContainerStyle}>
              <div
                style={getRoleOptionStyle(role === "lab")}
                onClick={() => setRole("lab")}
              >
                <input
                  style={radioStyle}
                  type="radio"
                  name="role"
                  value="lab"
                  checked={role === "lab"}
                  onChange={() => setRole("lab")}
                />
                <span style={roleLabelStyle}>Laboratory</span>
              </div>

              <div
                style={getRoleOptionStyle(role === "doctor")}
                onClick={() => setRole("doctor")}
              >
                <input
                  style={radioStyle}
                  type="radio"
                  name="role"
                  value="doctor"
                  checked={role === "doctor"}
                  onChange={() => setRole("doctor")}
                />
                <span style={roleLabelStyle}>Doctor</span>
              </div>
            </div>
          </div>

          {error && (
            <div style={errorContainerStyle}>
              <svg
                style={errorIconStyle}
                fill="currentColor"
                viewBox="0 0 20 20"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  fillRule="evenodd"
                  d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z"
                  clipRule="evenodd"
                ></path>
              </svg>
              {error}
            </div>
          )}

          <div style={buttonWrapperStyle}>
            <Button
              type="submit"
              variant="primary"
              fullWidth
              disabled={loading}
              style={{
                padding: isMobile ? "12px" : "14px",
                fontSize: isMobile ? "15px" : "16px",
                borderRadius: "8px",
                width: "100%",
              }}
            >
              {loading ? (
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                  }}
                >
                  <Spinner size="small" color="white" />
                  <span style={{ marginLeft: "10px" }}>Signing in...</span>
                </div>
              ) : (
                "Sign in"
              )}
            </Button>
          </div>

          <div style={demoTextStyle}>
            For demo: use any username and password
          </div>
        </form>
      </div>

      <div style={footerStyle}>
        &copy; {new Date().getFullYear()} MedSec System. All rights reserved.
      </div>
    </div>
  );
};

export default Login;
