import React, { createContext, useContext, useState } from "react";
import { useAuth } from "./AuthContext";
import { initializeDHAuth } from "../utils/diffieHellman";

// Create the patient context
const PatientContext = createContext();

// API base URL
const API_BASE_URL = "http://localhost:8000/api/patients";

// Patient provider component
export const PatientProvider = ({ children }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { tokens, refreshToken, dhCredentials } = useAuth();

  // Helper function to handle API requests with token refresh
  const apiRequest = async (url, method, body = null, isFormData = false) => {
    setLoading(true);
    setError(null);

    try {
      let headers = {
        Authorization: `Bearer ${tokens.access}`,
      };

      if (!isFormData) {
        headers["Content-Type"] = "application/json";
      }

      const requestOptions = {
        method,
        headers,
        body: isFormData ? body : body ? JSON.stringify(body) : null,
      };

      console.log(`Making ${method} request to: ${url}`);
      let response = await fetch(url, requestOptions);

      // If token expired, try to refresh it
      if (response.status === 401) {
        console.log("Token expired, attempting to refresh");
        const refreshed = await refreshToken();
        if (refreshed) {
          // Update headers with new token
          headers.Authorization = `Bearer ${localStorage.getItem(
            "accessToken"
          )}`;
          // Retry the request
          console.log("Token refreshed, retrying request");
          response = await fetch(url, {
            ...requestOptions,
            headers,
          });
        }
      }

      // Handle 404 errors specifically
      if (response.status === 404) {
        console.error(`Resource not found: ${url}`);
        setLoading(false);
        setError("Resource not found. Please check the patient ID.");
        return { success: false, error: "Resource not found" };
      }

      const data = await response.json();
      console.log("Response data:", data);
      setLoading(false);

      if (!response.ok) {
        setError(data.detail || "Operation failed");
        return { success: false, error: data.detail || "Operation failed" };
      }

      return { success: true, data };
    } catch (err) {
      console.error("API request error:", err);
      setLoading(false);
      setError("Network error or server unavailable");
      return { success: false, error: "Network error" };
    }
  };

  // Add new patient
  const addPatient = async (patientData) => {
    const formData = new FormData();
    formData.append("id", patientData.id);
    formData.append("name", patientData.name);
    formData.append("age", patientData.age);

    // Handle notes field (optional)
    if (patientData.notes) {
      formData.append("note", patientData.notes);
    }

    // Handle image if available
    if (patientData.image) {
      formData.append("image", patientData.image);
    }

    const result = await apiRequest(
      `${API_BASE_URL}/patients/`,
      "POST",
      formData,
      true
    );

    return result.success ? result.data : null;
  };

  // Search for patient by ID
  const findPatientById = async (id) => {
    try {
      console.log(`Searching for patient with ID: ${id}`);
      const result = await apiRequest(`${API_BASE_URL}/patients/${id}/`, "GET");

      if (result.success) {
        console.log("Patient found:", result.data);
        return result.data;
      } else {
        console.error("Error finding patient:", result.error);
        return null;
      }
    } catch (err) {
      console.error("Exception in findPatientById:", err);
      return null;
    }
  };

  // Fetch image with Diffie-Hellman authentication
  const fetchSecureImage = async (imageUrl) => {
    setLoading(true);
    setError(null);

    try {
      // Ensure we have DH credentials
      let credentials = dhCredentials;

      // If no credentials exist, initialize them
      if (!credentials) {
        console.log("No DH credentials found, initializing new ones");
        try {
          credentials = await initializeDHAuth();
        } catch (dhInitError) {
          console.error("Failed to initialize DH authentication:", dhInitError);

          // Check for specific errors mentioned in the documentation
          if (dhInitError.message.includes("Failed to get DH params")) {
            throw new Error(
              "Could not connect to DH parameters endpoint. Please check your network connection."
            );
          } else if (dhInitError.message.includes("Failed to exchange keys")) {
            throw new Error(
              "Key exchange failed. The server may be unavailable or rejecting the request."
            );
          } else {
            throw new Error(
              "Failed to initialize Diffie-Hellman authentication: " +
                dhInitError.message
            );
          }
        }
      }

      if (!credentials) {
        throw new Error("Failed to obtain Diffie-Hellman credentials");
      }

      // Check if the URL is already using the new endpoint structure
      let url = imageUrl;
      if (!url.includes("/api/patients/image/")) {
        // Convert old URL format to new format if needed
        const patientId = extractPatientIdFromUrl(url);
        if (patientId) {
          url = `${API_BASE_URL}/image/${patientId}/`;
          console.log("Converted image URL to new format:", url);
        }
      }

      // Add cache-busting parameter
      const cacheParam = `?nocache=${new Date().getTime()}`;
      url = `${url}${cacheParam}`;

      console.log("Fetching secure image with DH authentication:", url);

      // Make request with both JWT and DH authentication
      const response = await fetch(url, {
        headers: {
          Authorization: `Bearer ${tokens.access}`,
          "X-DH-Key": credentials.sharedSecret,
          "X-Client-ID": credentials.clientId,
        },
        cache: "no-store",
      });

      // Handle specific error cases from documentation
      if (response.status === 401) {
        console.error("Authentication failed (401) when fetching image");

        // Try to regenerate DH credentials and retry once
        try {
          console.log("Attempting to regenerate DH credentials and retry");
          const newCredentials = await initializeDHAuth();

          // Retry with new credentials
          const retryResponse = await fetch(url, {
            headers: {
              Authorization: `Bearer ${tokens.access}`,
              "X-DH-Key": newCredentials.sharedSecret,
              "X-Client-ID": newCredentials.clientId,
            },
            cache: "no-store",
          });

          if (!retryResponse.ok) {
            throw new Error(
              `Image retrieval failed after credential refresh: ${retryResponse.status} ${retryResponse.statusText}`
            );
          }

          const blob = await retryResponse.blob();
          const objectUrl = URL.createObjectURL(blob);
          setLoading(false);
          return objectUrl;
        } catch (retryError) {
          throw new Error(
            "Authentication failed. Please try logging out and back in."
          );
        }
      }

      if (!response.ok) {
        throw new Error(
          `Failed to load image: ${response.status} ${response.statusText}`
        );
      }

      const blob = await response.blob();
      const objectUrl = URL.createObjectURL(blob);

      setLoading(false);
      return objectUrl;
    } catch (err) {
      setLoading(false);
      setError(`Error fetching secure image: ${err.message}`);
      console.error("Error fetching secure image:", err);
      return null;
    }
  };

  // Helper function to extract patient ID from image URL
  const extractPatientIdFromUrl = (url) => {
    // Try to extract patient ID from various URL formats
    const regex = /\/patients\/([^/]+)\/|\/image\/([^/]+)\//;
    const match = url.match(regex);
    if (match) {
      return match[1] || match[2];
    }
    return null;
  };

  const value = {
    loading,
    error,
    addPatient,
    findPatientById,
    fetchSecureImage,
  };

  return (
    <PatientContext.Provider value={value}>{children}</PatientContext.Provider>
  );
};

// Custom hook for using patient context
export const usePatients = () => {
  const context = useContext(PatientContext);
  if (!context) {
    throw new Error("usePatients must be used within a PatientProvider");
  }
  return context;
};
