import React, { useState } from "react";
import Layout from "../../components/Layout";
import Input from "../../components/Input";
import Button from "../../components/Button";
import Spinner from "../../components/Spinner";
import { usePatients } from "../../context/PatientContext";

const SearchPatient = () => {
  const [patientId, setPatientId] = useState("");
  const [searchError, setSearchError] = useState("");
  const [patient, setPatient] = useState(null);
  const { findPatientById, loading } = usePatients();

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!patientId.trim()) {
      setSearchError("Please enter a patient ID");
      return;
    }

    try {
      const foundPatient = await findPatientById(patientId);

      if (!foundPatient) {
        setSearchError("No patient found with this ID");
        setPatient(null);
      } else {
        setPatient(foundPatient);
        setSearchError("");
      }
    } catch (err) {
      console.error("Error searching for patient:", err);
      setSearchError("An error occurred while searching");
    }
  };

  return (
    <Layout>
      <div className="max-w-4xl mx-auto">
        <div className="mb-6">
          <h1 className="text-2xl font-bold text-gray-800">Search Patient</h1>
          <p className="text-gray-600">
            Enter the patient ID to view their details and test results
          </p>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md mb-6">
          <form
            onSubmit={handleSearch}
            className="flex flex-col md:flex-row gap-4 items-end"
          >
            <div className="flex-grow">
              <Input
                label="Patient ID"
                id="patientId"
                value={patientId}
                onChange={(e) => {
                  setPatientId(e.target.value);
                  setSearchError("");
                }}
                placeholder="Enter patient ID (e.g., P001)"
                error={searchError}
              />
            </div>
            <div>
              <Button type="submit" variant="primary" disabled={loading}>
                {loading ? <Spinner size="small" color="white" /> : "Search"}
              </Button>
            </div>
          </form>
        </div>

        {patient && (
          <div className="bg-white p-6 rounded-lg shadow-md">
            <div className="flex flex-col md:flex-row gap-6">
              <div className="md:w-1/3">
                {patient.image ? (
                  <div className="text-center">
                    <img
                      src={patient.image}
                      alt={patient.name}
                      className="rounded-lg shadow-sm w-full h-auto max-h-64 object-cover"
                    />
                  </div>
                ) : (
                  <div className="rounded-lg bg-gray-100 flex items-center justify-center h-48">
                    <p className="text-gray-500">No image available</p>
                  </div>
                )}
              </div>

              <div className="md:w-2/3">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <h3 className="text-sm font-medium text-gray-500">
                      Patient ID
                    </h3>
                    <p className="text-lg font-semibold">{patient.id}</p>
                  </div>

                  <div>
                    <h3 className="text-sm font-medium text-gray-500">Age</h3>
                    <p className="text-lg">{patient.age} years</p>
                  </div>

                  <div className="col-span-2">
                    <h3 className="text-sm font-medium text-gray-500">
                      Full Name
                    </h3>
                    <p className="text-lg font-semibold">{patient.name}</p>
                  </div>
                </div>

                <hr className="my-4" />

                <div>
                  <h3 className="text-sm font-medium text-gray-500 mb-2">
                    Test Results
                  </h3>
                  {patient.results ? (
                    <div className="bg-blue-50 text-blue-800 p-4 rounded-md">
                      {patient.results}
                    </div>
                  ) : (
                    <p className="text-gray-500 italic">
                      No test results available
                    </p>
                  )}
                </div>

                <div className="mt-6 flex justify-end">
                  <Button
                    variant="secondary"
                    size="small"
                    onClick={() => {
                      setPatient(null);
                      setPatientId("");
                    }}
                  >
                    Clear Results
                  </Button>
                </div>
              </div>
            </div>
          </div>
        )}

        {!patient && !loading && patientId && searchError && (
          <div className="bg-white p-6 rounded-lg shadow-md text-center">
            <div className="text-red-600 mb-2">
              <svg
                className="w-12 h-12 mx-auto"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                />
              </svg>
            </div>
            <h3 className="text-lg font-medium text-gray-900">
              No Results Found
            </h3>
            <p className="text-gray-500 mt-1">
              We couldn't find a patient with the ID:{" "}
              <span className="font-semibold">{patientId}</span>
            </p>
            <div className="mt-4">
              <Button
                variant="secondary"
                size="small"
                onClick={() => {
                  setPatientId("");
                  setSearchError("");
                }}
              >
                Try Again
              </Button>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default SearchPatient;
