import React, { createContext, useContext, useState, useEffect } from "react";

// Create the auth context
const AuthContext = createContext();

// Auth provider component
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Auto-login for testing purposes
  useEffect(() => {
    // Uncomment the following line to auto-login for testing
    // For doctor role: autoLogin("doctor"); For lab role: autoLogin("lab");
    // autoLogin("lab");
  }, []);

  // Auto-login helper function
  const autoLogin = (role) => {
    setUser({
      id: "test_user",
      username: "Test User",
      role: role,
    });
  };

  // Login function (simulated)
  const login = (username, password, role) => {
    setLoading(true);
    setError(null);

    // Simulate API call
    setTimeout(() => {
      // For demonstration purposes - accept any username/password combo
      if (username && password) {
        setUser({
          id: "user123",
          username,
          role: role, // 'lab' or 'doctor'
        });
        setLoading(false);
      } else {
        setError("Invalid username or password");
        setLoading(false);
      }
    }, 1000);
  };

  // Logout function
  const logout = () => {
    setUser(null);
  };

  const value = {
    user,
    loading,
    error,
    login,
    logout,
    isAuthenticated: !!user,
    isLab: user?.role === "lab",
    isDoctor: user?.role === "doctor",
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Custom hook for using auth context
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};
