import React, { useState } from "react";
import Layout from "../../components/Layout";
import Input from "../../components/Input";
import Button from "../../components/Button";
import Spinner from "../../components/Spinner";
import { usePatients } from "../../context/PatientContext";

const SearchPatient = () => {
  const [patientId, setPatientId] = useState("");
  const [searchError, setSearchError] = useState("");
  const [patient, setPatient] = useState(null);
  const { findPatientById, loading } = usePatients();

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!patientId.trim()) {
      setSearchError("Please enter a patient ID");
      return;
    }

    try {
      const foundPatient = await findPatientById(patientId);

      if (!foundPatient) {
        setSearchError("No patient found with this ID");
        setPatient(null);
      } else {
        setPatient(foundPatient);
        setSearchError("");
      }
    } catch (err) {
      console.error("Error searching for patient:", err);
      setSearchError("An error occurred while searching");
    }
  };

  return (
    <Layout>
      <div className="max-w-4xl mx-auto px-4 py-6">
        <div className="mb-8">
          <h1 className="text-2xl font-bold text-gray-800 border-l-4 border-blue-600 pl-3">
            Patient Lookup
          </h1>
          <p className="text-gray-600 mt-2">
            Enter the patient ID to view their details and test results
          </p>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md mb-8">
          <form onSubmit={handleSearch}>
            <div className="flex flex-col md:flex-row gap-4 items-end">
              <div className="flex-grow">
                <Input
                  label="Patient ID"
                  id="patientId"
                  value={patientId}
                  onChange={(e) => {
                    setPatientId(e.target.value);
                    setSearchError("");
                  }}
                  placeholder="Enter patient ID (e.g., P001)"
                  error={searchError}
                />
              </div>
              <div className="md:pb-4">
                <Button
                  type="submit"
                  variant="primary"
                  disabled={loading}
                  className="w-full md:w-auto px-6"
                >
                  {loading ? (
                    <div className="flex items-center justify-center">
                      <Spinner size="small" color="white" />
                      <span className="ml-2">Searching...</span>
                    </div>
                  ) : (
                    <div className="flex items-center">
                      <svg
                        className="w-4 h-4 mr-2"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                        />
                      </svg>
                      Search
                    </div>
                  )}
                </Button>
              </div>
            </div>
          </form>
        </div>

        {patient && (
          <div className="bg-white rounded-lg shadow-md overflow-hidden">
            <div className="flex flex-col md:flex-row">
              <div className="md:w-1/3 bg-gray-50">
                {patient.image ? (
                  <div className="h-full">
                    <img
                      src={patient.image}
                      alt={patient.name}
                      className="w-full h-full object-cover"
                    />
                  </div>
                ) : (
                  <div className="flex items-center justify-center h-64 md:h-full bg-gray-100 p-6">
                    <div className="text-center text-gray-500">
                      <svg
                        className="w-16 h-16 mx-auto mb-2 text-gray-400"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                        />
                      </svg>
                      <p>No image available</p>
                    </div>
                  </div>
                )}
              </div>

              <div className="md:w-2/3 p-6">
                <div className="flex justify-between items-start mb-6">
                  <h2 className="text-2xl font-bold text-gray-800">
                    {patient.name}
                  </h2>
                  <div className="bg-blue-100 text-blue-800 text-xs px-3 py-1 rounded-full font-medium">
                    Patient ID: {patient.id}
                  </div>
                </div>

                <div className="mb-6">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <h3 className="text-sm font-medium text-gray-500">Age</h3>
                      <p className="text-lg">{patient.age} years</p>
                    </div>
                  </div>
                </div>

                <div className="border-t border-gray-200 pt-4">
                  <h3 className="text-lg font-semibold text-gray-800 mb-3">
                    Test Results
                  </h3>
                  {patient.results ? (
                    <div className="bg-blue-50 text-blue-800 p-4 rounded-md">
                      {patient.results}
                    </div>
                  ) : (
                    <p className="text-gray-500 italic">
                      No test results available
                    </p>
                  )}
                </div>

                <div className="mt-6 flex justify-end">
                  <Button
                    variant="secondary"
                    size="small"
                    onClick={() => {
                      setPatient(null);
                      setPatientId("");
                    }}
                  >
                    Clear Results
                  </Button>
                </div>
              </div>
            </div>
          </div>
        )}

        {!patient && !loading && patientId && searchError && (
          <div className="bg-white p-6 rounded-lg shadow-md text-center">
            <div className="text-red-600 mb-4">
              <svg
                className="w-12 h-12 mx-auto"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                />
              </svg>
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              No Results Found
            </h3>
            <p className="text-gray-500 mb-4">
              We couldn't find a patient with the ID:{" "}
              <span className="font-semibold">{patientId}</span>
            </p>
            <Button
              variant="secondary"
              size="small"
              onClick={() => {
                setPatientId("");
                setSearchError("");
              }}
            >
              Try Again
            </Button>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default SearchPatient;
