import React, { useState, useEffect } from "react";
import Layout from "../../components/Layout";
import Button from "../../components/Button";
import Spinner from "../../components/Spinner";
import { usePatients } from "../../context/PatientContext";
import { useAuth } from "../../context/AuthContext";

// Error display component for troubleshooting
const ErrorDisplay = ({ error, onRetry, onClear }) => {
  const [showDetails, setShowDetails] = useState(false);

  // Extract error details if available
  const errorMessage =
    typeof error === "string" ? error : error?.message || "Unknown error";
  const isNetworkError =
    errorMessage.toLowerCase().includes("network") ||
    errorMessage.toLowerCase().includes("connect");
  const isAuthError =
    errorMessage.toLowerCase().includes("auth") ||
    errorMessage.toLowerCase().includes("401");
  const isNotFoundError =
    errorMessage.toLowerCase().includes("not found") ||
    errorMessage.toLowerCase().includes("404");

  // Style for error container
  const errorContainerStyle = {
    backgroundColor: "#FEF2F2",
    border: "1px solid #F87171",
    borderRadius: "8px",
    padding: "16px",
    marginBottom: "24px",
  };

  // Style for error heading
  const errorHeadingStyle = {
    color: "#B91C1C",
    fontSize: "16px",
    fontWeight: "600",
    marginBottom: "8px",
    display: "flex",
    alignItems: "center",
  };

  // Style for error message
  const errorMessageStyle = {
    color: "#7F1D1D",
    fontSize: "14px",
    marginBottom: "16px",
  };

  // Style for error details
  const errorDetailsStyle = {
    backgroundColor: "#FEE2E2",
    padding: "12px",
    borderRadius: "6px",
    fontSize: "13px",
    fontFamily: "monospace",
    whiteSpace: "pre-wrap",
    marginBottom: "16px",
    maxHeight: "100px",
    overflowY: "auto",
  };

  // Style for action buttons
  const actionsStyle = {
    display: "flex",
    gap: "8px",
  };

  // Determine error icon and title
  let errorIcon = (
    <svg
      width="20"
      height="20"
      fill="none"
      stroke="currentColor"
      viewBox="0 0 24 24"
      xmlns="http://www.w3.org/2000/svg"
    >
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
      />
    </svg>
  );

  let errorTitle = "Error";

  if (isNetworkError) {
    errorTitle = "Network Error";
    errorIcon = (
      <svg
        width="20"
        height="20"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth={2}
          d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6"
        />
      </svg>
    );
  } else if (isAuthError) {
    errorTitle = "Authentication Error";
    errorIcon = (
      <svg
        width="20"
        height="20"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth={2}
          d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z"
        />
      </svg>
    );
  } else if (isNotFoundError) {
    errorTitle = "Not Found Error";
    errorIcon = (
      <svg
        width="20"
        height="20"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth={2}
          d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
        />
      </svg>
    );
  }

  // Troubleshooting tips based on error type
  let troubleshootingTips = "";

  if (isNetworkError) {
    troubleshootingTips =
      "• Check your internet connection\n• Verify the server is running\n• Confirm API base URL is correct";
  } else if (isAuthError) {
    troubleshootingTips =
      "• Try logging out and back in\n• Your session may have expired\n• Check that your account has proper permissions";
  } else if (isNotFoundError) {
    troubleshootingTips =
      "• Verify the patient ID is correct\n• Check API endpoints in the code\n• Ensure the patient exists in the database";
  }

  return (
    <div style={errorContainerStyle}>
      <div style={errorHeadingStyle}>
        <span style={{ marginRight: "8px" }}>{errorIcon}</span>
        {errorTitle}
      </div>
      <div style={errorMessageStyle}>{errorMessage}</div>

      {troubleshootingTips && (
        <div>
          <div style={{ fontWeight: "500", marginBottom: "4px" }}>
            Troubleshooting:
          </div>
          <div style={errorDetailsStyle}>{troubleshootingTips}</div>
        </div>
      )}

      <div style={actionsStyle}>
        {onRetry && (
          <Button
            variant="danger"
            size="small"
            onClick={onRetry}
            style={{ padding: "8px 12px", fontSize: "13px" }}
          >
            Retry
          </Button>
        )}

        {onClear && (
          <Button
            variant="secondary"
            size="small"
            onClick={onClear}
            style={{ padding: "8px 12px", fontSize: "13px" }}
          >
            Clear
          </Button>
        )}

        <Button
          variant="secondary"
          size="small"
          onClick={() => setShowDetails(!showDetails)}
          style={{ padding: "8px 12px", fontSize: "13px", marginLeft: "auto" }}
        >
          {showDetails ? "Hide Details" : "Show Details"}
        </Button>
      </div>

      {showDetails && (
        <div style={{ marginTop: "12px" }}>
          <div style={{ fontWeight: "500", marginBottom: "4px" }}>
            Technical Details:
          </div>
          <div style={errorDetailsStyle}>
            Error: {JSON.stringify(error, null, 2)}
          </div>
        </div>
      )}
    </div>
  );
};

const SearchPatient = () => {
  const [patientId, setPatientId] = useState("");
  const [searchError, setSearchError] = useState("");
  const [patient, setPatient] = useState(null);
  const [isImageEnlarged, setIsImageEnlarged] = useState(false);
  const [imageBlob, setImageBlob] = useState(null);
  const [imageLoading, setImageLoading] = useState(false);
  const [imageError, setImageError] = useState(null);
  const { findPatientById, loading, fetchSecureImage } = usePatients();
  const { tokens } = useAuth();

  // Add responsive state
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const isMobile = windowWidth < 768;
  const isTablet = windowWidth >= 768 && windowWidth < 1024;

  // Handle window resize for responsiveness
  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Fetch image with secure authentication when patient changes and has an image_url
  useEffect(() => {
    if (patient?.image_url) {
      fetchSecureImageWithHandling(patient.image_url);
    } else {
      setImageBlob(null);
    }
  }, [patient]);

  // Function to fetch the image with DH authentication and handle states
  const fetchSecureImageWithHandling = async (url) => {
    setImageLoading(true);
    setImageError(null);

    try {
      const formattedUrl = formatImageUrl(url);
      console.log("Fetching secure image from:", formattedUrl);

      const objectUrl = await fetchSecureImage(formattedUrl);

      if (objectUrl) {
        setImageBlob(objectUrl);
        setImageLoading(false);
      } else {
        throw new Error("Failed to retrieve image");
      }
    } catch (error) {
      console.error("Error fetching image:", error);

      // Provide more specific error messages based on documentation
      if (error.message.includes("Authentication failed")) {
        setImageError(
          "Authentication error: Please try logging out and back in"
        );
      } else if (
        error.message.includes("network") ||
        error.message.includes("connect")
      ) {
        setImageError("Network error: Unable to connect to image server");
      } else if (error.message.includes("401")) {
        setImageError("Access denied: Your session may have expired");
      } else {
        setImageError(error.message);
      }

      setImageLoading(false);
    }
  };

  // Cleanup object URL when component unmounts or when imageBlob changes
  useEffect(() => {
    return () => {
      if (imageBlob) {
        URL.revokeObjectURL(imageBlob);
      }
    };
  }, [imageBlob]);

  const handleSearch = async (e) => {
    e.preventDefault();

    // Clear previous results
    setPatient(null);
    setImageBlob(null);
    setImageError(null);

    if (!patientId.trim()) {
      setSearchError("Please enter a patient ID");
      return;
    }

    try {
      // Make a fresh API request every time
      const foundPatient = await findPatientById(patientId);

      if (!foundPatient) {
        setSearchError("No patient found with this ID");
        setPatient(null);
      } else {
        setPatient(foundPatient);
        setSearchError("");
      }
    } catch (err) {
      console.error("Error searching for patient:", err);
      setSearchError("An error occurred while searching");
    }
  };

  // ==== STYLES ====

  // Page container styles
  const pageContainerStyle = {
    maxWidth: isMobile ? "100%" : isTablet ? "90%" : "1100px",
    margin: "0 auto",
    padding: isMobile ? "16px" : isTablet ? "24px" : "32px",
    width: "100%",
    boxSizing: "border-box",
  };

  // Header section styles
  const headerSectionStyle = {
    marginBottom: isMobile ? "24px" : "32px",
  };

  // Header title styles
  const headerTitleStyle = {
    fontSize: isMobile ? "20px" : isTablet ? "24px" : "28px",
    fontWeight: "700",
    color: "#1E293B",
    borderLeft: "4px solid #2563EB",
    paddingLeft: "12px",
    marginBottom: "8px",
  };

  // Header description styles
  const headerDescriptionStyle = {
    fontSize: isMobile ? "14px" : "16px",
    color: "#64748B",
    marginTop: "8px",
  };

  // Search form card styles
  const searchFormCardStyle = {
    backgroundColor: "#ffffff",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.05)",
    padding: isMobile ? "16px" : "24px",
    marginBottom: "24px",
    border: "1px solid #E2E8F0",
  };

  // Form styles
  const formStyle = {
    display: "flex",
    flexDirection: isMobile ? "column" : "row",
    gap: "16px",
    alignItems: isMobile ? "stretch" : "flex-end",
    width: "100%",
  };

  // Form group styles
  const formGroupStyle = {
    flexGrow: 1,
    position: "relative",
  };

  // Label styles
  const labelStyle = {
    display: "block",
    marginBottom: "8px",
    fontWeight: "500",
    fontSize: isMobile ? "14px" : "15px",
    color: "#334155",
  };

  // Input styles
  const inputStyle = {
    width: "100%",
    padding: "12px 16px",
    fontSize: isMobile ? "14px" : "15px",
    borderRadius: "8px",
    border: searchError ? "1px solid #EF4444" : "1px solid #CBD5E1",
    backgroundColor: "#FFFFFF",
    boxSizing: "border-box",
    outline: "none",
    transition: "border-color 0.2s ease, box-shadow 0.2s ease",
  };

  // Input focus/blur handlers
  const focusStyle = (e) => {
    e.target.style.borderColor = "#3B82F6";
    e.target.style.boxShadow = "0 0 0 3px rgba(59, 130, 246, 0.1)";
  };

  const blurStyle = (e) => {
    if (searchError) {
      e.target.style.borderColor = "#EF4444";
      e.target.style.boxShadow = "none";
    } else {
      e.target.style.borderColor = "#CBD5E1";
      e.target.style.boxShadow = "none";
    }
  };

  // Error message styles
  const errorMessageStyle = {
    color: "#EF4444",
    fontSize: "13px",
    marginTop: "4px",
    position: isMobile ? "static" : "absolute",
    paddingBottom: isMobile ? "4px" : "0",
  };

  // Button container styles
  const buttonContainerStyle = {
    display: "flex",
    alignItems: "center",
  };

  // Search button icon styles
  const searchIconStyle = {
    width: "16px",
    height: "16px",
    marginRight: "8px",
  };

  // Patient card container styles
  const patientCardStyle = {
    backgroundColor: "#FFFFFF",
    borderRadius: "12px",
    overflow: "hidden",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.08)",
    border: "1px solid #E2E8F0",
    marginBottom: "24px",
  };

  // Patient card flex container
  const patientCardFlexStyle = {
    display: "flex",
    flexDirection: isMobile ? "column" : "row",
  };

  // Patient image container styles
  const patientImageContainerStyle = {
    width: isMobile ? "100%" : "33.333%",
    backgroundColor: "#F8FAFC",
    cursor: patient?.image_url ? "pointer" : "default",
    position: "relative",
    overflow: "hidden",
  };

  // No image placeholder styles
  const noImagePlaceholderStyle = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: isMobile ? "200px" : "100%",
    backgroundColor: "#F1F5F9",
    padding: "24px",
  };

  // No image icon styles
  const noImageIconStyle = {
    width: "64px",
    height: "64px",
    marginBottom: "8px",
    color: "#94A3B8",
  };

  // Patient info container styles
  const patientInfoContainerStyle = {
    width: isMobile ? "100%" : "66.666%",
    padding: isMobile ? "20px" : "24px",
  };

  // Patient header styles
  const patientHeaderStyle = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: isMobile ? "flex-start" : "center",
    marginBottom: "24px",
    flexDirection: isMobile ? "column" : "row",
    gap: isMobile ? "12px" : "0",
  };

  // Patient name styles
  const patientNameStyle = {
    fontSize: isMobile ? "20px" : "24px",
    fontWeight: "700",
    color: "#1E293B",
  };

  // Patient ID badge styles
  const patientIdBadgeStyle = {
    backgroundColor: "#DBEAFE",
    color: "#1D4ED8",
    fontSize: "12px",
    padding: "4px 12px",
    borderRadius: "9999px",
    fontWeight: "500",
    display: "inline-block",
  };

  // Patient details grid styles
  const patientDetailsGridStyle = {
    display: "grid",
    gridTemplateColumns: isMobile ? "1fr" : "repeat(2, 1fr)",
    gap: "16px",
    marginBottom: "24px",
    borderBottom: "1px solid #E2E8F0",
    paddingBottom: "16px",
  };

  // Patient detail label styles
  const patientDetailLabelStyle = {
    fontSize: "13px",
    fontWeight: "500",
    color: "#64748B",
    marginBottom: "4px",
  };

  // Patient detail value styles
  const patientDetailValueStyle = {
    fontSize: isMobile ? "16px" : "18px",
    color: "#334155",
    fontWeight: "500",
  };

  // Patient results section styles
  const patientNotesSectionStyle = {
    paddingTop: "16px",
    width: isMobile ? "90%" : "100%",
  };

  // Patient results title styles
  const patientNotesTitleStyle = {
    fontSize: isMobile ? "16px" : "18px",
    fontWeight: "600",
    color: "#1E293B",
    marginBottom: "12px",
  };

  // Test results content styles
  const notesContentStyle = {
    backgroundColor: "#EFF6FF",
    color: "#1E40AF",
    padding: "16px",
    borderRadius: "8px",
    fontSize: isMobile ? "14px" : "15px",
    lineHeight: "1.5",
    marginBottom: "20px",
    minHeight: "40px",
    display: "flex",
    alignItems: "center",
  };

  // No results message styles
  const noNotesMessageStyle = {
    color: "#64748B",
    fontStyle: "italic",
  };

  // Actions container styles
  const actionsContainerStyle = {
    marginTop: "12px",
    display: "flex",
    justifyContent: isMobile ? "center" : "flex-end",
    gap: "12px",
    flexDirection: isMobile ? "column" : "row",
    width: isMobile ? "90%" : "100%",
  };

  // No patient found card styles
  const noPatientCardStyle = {
    backgroundColor: "#FFFFFF",
    padding: "24px",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.05)",
    textAlign: "center",
    border: "1px solid #E2E8F0",
    marginBottom: "24px",
  };

  // No patient icon styles
  const noPatientIconStyle = {
    width: "48px",
    height: "48px",
    margin: "0 auto 16px auto",
    color: "#EF4444",
  };

  // No patient title styles
  const noPatientTitleStyle = {
    fontSize: isMobile ? "16px" : "18px",
    fontWeight: "500",
    color: "#111827",
    marginBottom: "8px",
  };

  // No patient message styles
  const noPatientMessageStyle = {
    color: "#64748B",
    marginBottom: "16px",
  };

  // Patient ID highlight styles
  const patientIdHighlightStyle = {
    fontWeight: "600",
  };

  // Patient image zoom overlay
  const imageOverlayStyle = {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(0, 0, 0, 0.85)",
    display: isImageEnlarged ? "flex" : "none",
    justifyContent: "center",
    alignItems: "center",
    zIndex: 1000,
    backdropFilter: "blur(4px)",
  };

  // Enlarged image container
  const enlargedImageContainerStyle = {
    position: "relative",
    maxWidth: "90%",
    maxHeight: "90%",
    boxShadow: "0 4px 20px rgba(0, 0, 0, 0.3)",
    borderRadius: "8px",
    overflow: "hidden",
  };

  // Enlarged image style
  const enlargedImageStyle = {
    maxWidth: "100%",
    maxHeight: "90vh",
    objectFit: "contain",
  };

  // Close button for enlarged image
  const closeEnlargedImageButtonStyle = {
    position: "absolute",
    top: "12px",
    right: "12px",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    color: "white",
    borderRadius: "50%",
    width: "36px",
    height: "36px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    cursor: "pointer",
    border: "none",
    outline: "none",
  };

  // Image zoom icon
  const zoomIconStyle = {
    position: "absolute",
    top: "12px",
    right: "12px",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    color: "white",
    borderRadius: "50%",
    width: "32px",
    height: "32px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    cursor: "pointer",
    zIndex: 10,
  };

  // Patient image responsive style
  const patientImageStyle = {
    width: "100%",
    height: isMobile ? "250px" : "100%",
    objectFit: "cover",
    transition: "transform 0.3s ease",
  };

  // Hover text for image zoom
  const zoomTextStyle = {
    position: "absolute",
    bottom: "12px",
    left: "0",
    right: "0",
    textAlign: "center",
    backgroundColor: "rgba(0, 0, 0, 0.6)",
    color: "white",
    padding: "8px",
    fontSize: "12px",
    opacity: 0,
    transition: "opacity 0.2s ease",
  };

  // Function to format image URL
  const formatImageUrl = (imagePath) => {
    if (!imagePath) return null;

    // If URL is already absolute with http/https, use it as is
    if (imagePath.startsWith("http")) {
      return imagePath;
    }

    // If the path already includes the correct API structure, just add the base URL
    if (imagePath.startsWith("/api/patients/image/")) {
      return `http://localhost:8000${imagePath}`;
    }

    // Extract patient ID if possible
    const patientIdMatch = imagePath.match(
      /\/patients\/([^/]+)\/|\/([^/]+\.(jpg|jpeg|png|gif))/i
    );
    if (patientIdMatch) {
      const patientId = patientIdMatch[1] || patientIdMatch[2];
      return `http://localhost:8000/api/patients/image/${patientId}/`;
    }

    // Default fallback - use as is with API base URL
    return `http://localhost:8000${imagePath}`;
  };

  return (
    <Layout>
      <div style={pageContainerStyle}>
        <div style={headerSectionStyle}>
          <h1 style={headerTitleStyle}>Patient Lookup</h1>
          <p style={headerDescriptionStyle}>
            Enter the patient ID to view their details and test results
          </p>
        </div>

        {/* Display error with troubleshooting help if search error exists */}
        {searchError && (
          <ErrorDisplay
            error={searchError}
            onRetry={() => handleSearch({ preventDefault: () => {} })}
            onClear={() => {
              setSearchError("");
              setPatientId("");
            }}
          />
        )}

        <div style={searchFormCardStyle}>
          <form onSubmit={handleSearch} style={formStyle}>
            <div style={formGroupStyle}>
              <label style={labelStyle} htmlFor="patientId">
                Patient ID
              </label>
              <input
                id="patientId"
                type="text"
                style={inputStyle}
                value={patientId}
                onChange={(e) => {
                  setPatientId(e.target.value);
                  setSearchError("");
                }}
                placeholder="Enter patient ID (e.g., P001)"
                onFocus={focusStyle}
                onBlur={blurStyle}
              />
              {searchError && (
                <div style={errorMessageStyle}>{searchError}</div>
              )}
            </div>
            <div style={buttonContainerStyle}>
              <Button
                type="submit"
                variant="primary"
                disabled={loading}
                style={{
                  width: isMobile ? "100%" : "auto",
                  padding: "12px 20px",
                  fontSize: "15px",
                  borderRadius: "8px",
                  marginTop: isMobile && searchError ? "24px" : "0",
                  backgroundColor: "#0EA5E9",
                  borderColor: "#0EA5E9",
                }}
              >
                {loading ? (
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                    }}
                  >
                    <Spinner size="small" color="white" />
                    <span style={{ marginLeft: "8px" }}>Searching...</span>
                  </div>
                ) : (
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      justifyContent: isMobile ? "center" : "flex-start",
                    }}
                  >
                    <svg
                      style={searchIconStyle}
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                      />
                    </svg>
                    Search
                  </div>
                )}
              </Button>
            </div>
          </form>
        </div>

        {/* Patient Image Zoom Overlay */}
        <div
          style={imageOverlayStyle}
          onClick={() => setIsImageEnlarged(false)}
        >
          <div
            style={enlargedImageContainerStyle}
            onClick={(e) => e.stopPropagation()}
          >
            {imageBlob && (
              <img
                src={imageBlob}
                alt={patient?.name}
                style={enlargedImageStyle}
              />
            )}
            <button
              style={closeEnlargedImageButtonStyle}
              onClick={() => setIsImageEnlarged(false)}
              aria-label="Close image preview"
            >
              <svg
                width="20"
                height="20"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M6 18L18 6M6 6l12 12"
                />
              </svg>
            </button>
          </div>
        </div>

        {patient && (
          <div style={patientCardStyle}>
            <div style={patientCardFlexStyle}>
              <div
                style={patientImageContainerStyle}
                onMouseEnter={(e) => {
                  if (imageBlob) {
                    const zoomText =
                      e.currentTarget.querySelector("[data-zoom-text]");
                    if (zoomText) zoomText.style.opacity = "1";
                  }
                }}
                onMouseLeave={(e) => {
                  if (imageBlob) {
                    const zoomText =
                      e.currentTarget.querySelector("[data-zoom-text]");
                    if (zoomText) zoomText.style.opacity = "0";
                  }
                }}
              >
                {imageLoading ? (
                  <div style={noImagePlaceholderStyle}>
                    <Spinner size="medium" color="#64748B" />
                    <p style={{ marginTop: "16px", color: "#64748B" }}>
                      Loading image...
                    </p>
                  </div>
                ) : imageBlob ? (
                  <div style={{ height: "100%" }}>
                    <img
                      src={imageBlob}
                      alt={patient.name}
                      style={patientImageStyle}
                      onClick={() => setIsImageEnlarged(true)}
                    />
                    <div
                      style={zoomIconStyle}
                      onClick={() => setIsImageEnlarged(true)}
                    >
                      <svg
                        width="18"
                        height="18"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0zM10 7v3m0 0v3m0-3h3m-3 0H7"
                        />
                      </svg>
                    </div>
                    <div style={zoomTextStyle} data-zoom-text="true">
                      Click to enlarge image
                    </div>
                  </div>
                ) : imageError ? (
                  <div style={noImagePlaceholderStyle}>
                    <svg
                      style={noImageIconStyle}
                      fill="none"
                      stroke="#EF4444"
                      viewBox="0 0 24 24"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                      />
                    </svg>
                    <p style={{ color: "#EF4444" }}>Error loading image</p>
                  </div>
                ) : (
                  <div style={noImagePlaceholderStyle}>
                    <svg
                      style={noImageIconStyle}
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                      />
                    </svg>
                    <p>No image available</p>
                  </div>
                )}
              </div>

              <div style={patientInfoContainerStyle}>
                <div style={patientHeaderStyle}>
                  <h2 style={patientNameStyle}>{patient.name}</h2>
                  <div style={patientIdBadgeStyle}>
                    Patient ID: {patient.id}
                  </div>
                </div>

                <div style={patientDetailsGridStyle}>
                  <div>
                    <h3 style={patientDetailLabelStyle}>Age</h3>
                    <p style={patientDetailValueStyle}>{patient.age} years</p>
                  </div>
                  {patient.gender && (
                    <div>
                      <h3 style={patientDetailLabelStyle}>Gender</h3>
                      <p style={patientDetailValueStyle}>{patient.gender}</p>
                    </div>
                  )}
                  {patient.contactNumber && (
                    <div>
                      <h3 style={patientDetailLabelStyle}>Contact Number</h3>
                      <p style={patientDetailValueStyle}>
                        {patient.contactNumber}
                      </p>
                    </div>
                  )}
                  {patient.email && (
                    <div>
                      <h3 style={patientDetailLabelStyle}>Email</h3>
                      <p style={patientDetailValueStyle}>{patient.email}</p>
                    </div>
                  )}
                </div>

                <div style={patientNotesSectionStyle}>
                  <h3 style={patientNotesTitleStyle}>Notes</h3>
                  {patient.note ? (
                    <div style={notesContentStyle}>{patient.note}</div>
                  ) : (
                    <p style={noNotesMessageStyle}>No notes available</p>
                  )}
                </div>

                <div style={actionsContainerStyle}>
                  <Button
                    variant="secondary"
                    size="small"
                    onClick={() => {
                      setPatient(null);
                      setPatientId("");
                      setImageBlob(null);
                    }}
                    style={{
                      padding: "10px 16px",
                      fontSize: "14px",
                      borderRadius: "8px",
                      width: isMobile ? "100%" : "auto",
                      backgroundColor: "#F3E8FF",
                      color: "#9333EA",
                      border: "1px solid #E9D5FF",
                    }}
                  >
                    Clear Results
                  </Button>
                  {imageBlob && (
                    <Button
                      variant="primary"
                      size="small"
                      onClick={() => setIsImageEnlarged(true)}
                      style={{
                        padding: "10px 16px",
                        fontSize: "14px",
                        borderRadius: "8px",
                        width: isMobile ? "100%" : "auto",
                        backgroundColor: "#0EA5E9",
                        borderColor: "#0EA5E9",
                      }}
                    >
                      <div
                        style={{
                          display: "flex",
                          alignItems: "center",
                          justifyContent: isMobile ? "center" : "flex-start",
                        }}
                      >
                        <svg
                          style={{
                            width: "16px",
                            height: "16px",
                            marginRight: "6px",
                          }}
                          fill="none"
                          stroke="currentColor"
                          viewBox="0 0 24 24"
                          xmlns="http://www.w3.org/2000/svg"
                        >
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth={2}
                            d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"
                          />
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth={2}
                            d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"
                          />
                        </svg>
                        View Image
                      </div>
                    </Button>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}

        {!patient && !loading && patientId && searchError && (
          <div style={noPatientCardStyle}>
            <div style={{ color: "#EF4444" }}>
              <svg
                style={noPatientIconStyle}
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                />
              </svg>
            </div>
            <h3 style={noPatientTitleStyle}>No Results Found</h3>
            <p style={noPatientMessageStyle}>
              We couldn't find a patient with the ID:{" "}
              <span style={patientIdHighlightStyle}>{patientId}</span>
            </p>
            <Button
              variant="secondary"
              size="small"
              onClick={() => {
                setPatientId("");
                setSearchError("");
              }}
              style={{
                padding: "10px 16px",
                fontSize: "14px",
                borderRadius: "8px",
                width: isMobile ? "100%" : "auto",
                maxWidth: "200px",
                margin: isMobile ? "0 auto" : "0",
                backgroundColor: "#F3E8FF",
                color: "#9333EA",
                border: "1px solid #E9D5FF",
              }}
            >
              Try Again
            </Button>
          </div>
        )}

        {/* Display image error with troubleshooting help */}
        {imageError && patient && (
          <ErrorDisplay
            error={imageError}
            onRetry={() => fetchSecureImageWithHandling(patient.image_url)}
            onClear={() => setImageError(null)}
          />
        )}
      </div>
    </Layout>
  );
};

export default SearchPatient;
